pragma solidity 0.8.20;

import './IPool.sol';
import './ISlasher.sol';
import './IDelegationController.sol';

/**
 * @title Interface for the primary entrypoint for funds into BinLayer.
 * @notice See the `PoolController` contract itself for implementation details.
 */
interface IPoolController {
  /**
   * @notice Emitted when a new deposit occurs on behalf of `staker`.
   * @param staker Is the staker who is depositing funds into BinLayer.
   * @param pool Is the pool that `staker` has deposited into.
   * @param token Is the token that `staker` deposited.
   * @param shares Is the number of new shares `staker` has been granted in `pool`.
   */
  event Deposit(address staker, IERC20 token, IPool pool, uint256 shares);

  /// @notice Emitted when `thirdPartyTransfersForbidden` is updated for a pool and value by the owner
  event UpdatedThirdPartyTransfersForbidden(IPool pool, bool value);

  /// @notice Emitted when the `poolWhitelister` is changed
  event PoolWhitelisterChanged(address previousAddress, address newAddress);

  /// @notice Emitted when a pool is added to the approved list of pools for deposit
  event PoolAddedToDepositWhitelist(IPool pool);

  /// @notice Emitted when a pool is removed from the approved list of pools for deposit
  event PoolRemovedFromDepositWhitelist(IPool pool);

  /**
   * @notice Deposits `amount` of `token` into the specified `pool`, with the resultant shares credited to `msg.sender`
   * @param pool is the specified pool where deposit is to be made,
   * @param token is the denomination in which the deposit is to be made,
   * @param amount is the amount of token to be deposited in the pool by the staker
   * @return shares The amount of new shares in the `pool` created as part of the action.
   * @dev The `msg.sender` must have previously approved this contract to transfer at least `amount` of `token` on their behalf.
   * @dev Cannot be called by an address that is 'frozen' (this function will revert if the `msg.sender` is frozen).
   *
   * WARNING: Depositing tokens that allow reentrancy (eg. ERC-777) into a pool is not recommended.  This can lead to attack vectors
   *          where the token balance and corresponding pool shares are not in sync upon reentrancy.
   */
  function depositIntoPool(IPool pool, IERC20 token, uint256 amount) external returns (uint256 shares);

  /**
   * @notice Deposits `amount` of `token` into the specified `pool`, with the resultant shares credited to `staker`
   * @param staker Staker address
   * @param pool is the specified pool where deposit is to be made,
   * @param token is the denomination in which the deposit is to be made,
   * @param amount is the amount of token to be deposited in the pool by the staker
   * @return shares The amount of new shares in the `pool` created as part of the action.
   * @dev The `msg.sender` must have previously approved this contract to transfer at least `amount` of `token` on their behalf.
   * @dev Cannot be called by an address that is 'frozen' (this function will revert if the `msg.sender` is frozen).
   *
   * WARNING: Depositing tokens that allow reentrancy (eg. ERC-777) into a pool is not recommended.  This can lead to attack vectors
   *          where the token balance and corresponding pool shares are not in sync upon reentrancy.
   */
  function depositIntoPoolWithStaker(address staker, IPool pool, IERC20 token, uint256 amount) external returns (uint256 shares);

  /**
   * @notice Used for depositing an asset into the specified pool with the resultant shares credited to `staker`,
   * who must sign off on the action.
   * Note that the assets are transferred out/from the `msg.sender`, not from the `staker`; this function is explicitly designed
   * purely to help one address deposit 'for' another.
   * @param pool is the specified pool where deposit is to be made,
   * @param token is the denomination in which the deposit is to be made,
   * @param amount is the amount of token to be deposited in the pool by the staker
   * @param staker the staker that the deposited assets will be credited to
   * @param expiry the timestamp at which the signature expires
   * @param signature is a valid signature from the `staker`. either an ECDSA signature if the `staker` is an EOA, or data to forward
   * following EIP-1271 if the `staker` is a contract
   * @return shares The amount of new shares in the `pool` created as part of the action.
   * @dev The `msg.sender` must have previously approved this contract to transfer at least `amount` of `token` on their behalf.
   * @dev A signature is required for this function to eliminate the possibility of griefing attacks, specifically those
   * targeting stakers who may be attempting to undelegate.
   * @dev Cannot be called if thirdPartyTransfersForbidden is set to true for this pool
   *
   *  WARNING: Depositing tokens that allow reentrancy (eg. ERC-777) into a pool is not recommended.  This can lead to attack vectors
   *          where the token balance and corresponding pool shares are not in sync upon reentrancy
   */
  function depositIntoPoolWithSignature(
    IPool pool,
    IERC20 token,
    uint256 amount,
    address staker,
    uint256 expiry,
    bytes memory signature
  ) external returns (uint256 shares);

  /// @notice Used by the DelegationController.sol to remove a Staker's shares from a particular pool when entering the withdrawal queue
  function removeShares(address staker, IPool pool, uint256 shares) external;

  /// @notice Used by the DelegationController.sol to award a Staker some shares that have passed through the withdrawal queue
  function addShares(address staker, IERC20 token, IPool pool, uint256 shares) external;

  /// @notice Used by the DelegationController.sol to convert withdrawn shares to tokens and send them to a recipient
  function withdrawSharesAsTokens(address recipient, IPool pool, uint256 shares, IERC20 token) external;

  /// @notice Returns the current shares of `user` in `pool`
  function stakerPoolShares(address user, IPool pool) external view returns (uint256 shares);

  /**
   * @notice Get all details on the staker's deposits and corresponding shares
   * @return (staker's pools, shares in these pools)
   */
  function getDeposits(address staker) external view returns (IPool[] memory, uint256[] memory);

  /// @notice Simple getter function that returns `stakerPoolList[staker].length`.
  function stakerPoolListLength(address staker) external view returns (uint256);

  /**
   * @notice Owner-only function that adds the provided Pools to the 'whitelist' of pools that stakers can deposit into
   * @param poolsToWhitelist Pools that will be added to the `poolIsWhitelistedForDeposit` mapping (if they aren't in it already)
   * @param thirdPartyTransfersForbiddenValues bool values to set `thirdPartyTransfersForbidden` to for each pool
   */
  function addPoolsToDepositWhitelist(IPool[] calldata poolsToWhitelist, bool[] calldata thirdPartyTransfersForbiddenValues) external;

  /**
   * @notice Owner-only function that removes the provided Pools from the 'whitelist' of pools that stakers can deposit into
   * @param poolsToRemoveFromWhitelist Pools that will be removed to the `poolIsWhitelistedForDeposit` mapping (if they are in it)
   */
  function removePoolsFromDepositWhitelist(IPool[] calldata poolsToRemoveFromWhitelist) external;

  /// @notice Returns the single, central Delegation contract of BinLayer
  function delegation() external view returns (IDelegationController);

  /// @notice Returns the single, central Slasher contract of BinLayer
  function slasher() external view returns (ISlasher);

  /// @notice Returns the address of the `poolWhitelister`
  function poolWhitelister() external view returns (address);

  /// @notice Returns bool for whether or not `pool` is whitelisted for deposit
  function poolIsWhitelistedForDeposit(IPool pool) external view returns (bool);

  /**
   * @notice Returns bool for whether or not `pool` enables credit transfers. i.e enabling
   * depositIntoPoolWithSignature calls or queueing withdrawals to a different address than the staker.
   */
  function thirdPartyTransfersForbidden(IPool pool) external view returns (bool);
}
","keccak256":"0x2db650b42306ac72dc758828f01b962ff15c7b632b23dc7b60bd72806a01d206","license":"LGPL-3.0"},"contracts/interfaces/ISignatureUtils.sol":{"content":"// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

/**
 * @title The interface for common signature utilities.
 */
interface ISignatureUtils {
  // @notice Struct that bundles together a signature and an expiration time for the signature. Used primarily for stack management.
  struct SignatureWithExpiry {
    // the signature itself, formatted as a single bytes object
    bytes signature;
    // the expiration timestamp (UTC) of the signature
    uint256 expiry;
  }

  // @notice Struct that bundles together a signature, a salt for uniqueness, and an expiration time for the signature. Used primarily for stack management.
  struct SignatureWithSaltAndExpiry {
    // the signature itself, formatted as a single bytes object
    bytes signature;
    // the salt used to generate the signature
    bytes32 salt;
    // the expiration timestamp (UTC) of the signature
    uint256 expiry;
  }
}
","keccak256":"0xd2f901c14d08037588427527905fafbe1610b68a38d62c93925ea0f258f226a4","license":"LGPL-3.0"},"contracts/interfaces/ISlasher.sol":{"content":"// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import './IPoolController.sol';
import './IDelegationController.sol';

/**
 * @title Interface for the primary 'slashing' contract for BinLayer.
 * @notice See the `Slasher` contract itself for implementation details.
 */
interface ISlasher {
  // struct used to store information about the current state of an operator's obligations to middlewares they are serving
  struct MiddlewareTimes {
    // The update timestamp for the middleware whose most recent update was earliest, i.e. the 'stalest' update out of all middlewares the operator is serving
    uint32 stalestUpdateTimestamp;
    // The latest 'serveUntilTimestamp' from all of the middleware that the operator is serving
    uint32 latestServeUntilTimestamp;
  }

  // struct used to store details relevant to a single middleware that an operator has opted-in to serving
  struct MiddlewareDetails {
    // the timestamp at which the contract begins being able to finalize the operator's registration with the service via calling `recordFirstStakeUpdate`
    uint32 registrationMayBeginAtTimestamp;
    // the timestamp before which the contract is allowed to slash the user
    uint32 contractCanSlashOperatorUntilTimestamp;
    // the timestamp at which the middleware's view of the operator's stake was most recently updated
    uint32 latestUpdateTimestamp;
  }

  /// @notice Emitted when a middleware times is added to `operator`'s array.
  event MiddlewareTimesAdded(address operator, uint256 index, uint32 stalestUpdateTimestamp, uint32 latestServeUntilTimestamp);

  /// @notice Emitted when `operator` begins to allow `contractAddress` to slash them.
  event OptedIntoSlashing(address indexed operator, address indexed contractAddress);

  /// @notice Emitted when `contractAddress` signals that it will no longer be able to slash `operator` after the `contractCanSlashOperatorUntilTimestamp`.
  event SlashingAbilityRevoked(address indexed operator, address indexed contractAddress, uint32 contractCanSlashOperatorUntilTimestamp);

  /**
   * @notice Emitted when `slashingContract` 'freezes' the `slashedOperator`.
   * @dev The `slashingContract` must have permission to slash the `slashedOperator`, i.e. `canSlash(slasherOperator, slashingContract)` must return 'true'.
   */
  event OperatorFrozen(address indexed slashedOperator, address indexed slashingContract);

  /// @notice Emitted when `previouslySlashedAddress` is 'unfrozen', allowing them to again move deposited funds within BinLayer.
  event FrozenStatusReset(address indexed previouslySlashedAddress);

  /**
   * @notice Gives the `contractAddress` permission to slash the funds of the caller.
   * @dev Typically, this function must be called prior to registering for a middleware.
   */
  function optIntoSlashing(address contractAddress) external;

  /**
   * @notice Used for 'slashing' a certain operator.
   * @param toBeFrozen The operator to be frozen.
   * @dev Technically the operator is 'frozen' (hence the name of this function), and then subject to slashing pending a decision by a human-in-the-loop.
   * @dev The operator must have previously given the caller (which should be a contract) the ability to slash them, through a call to `optIntoSlashing`.
   */
  function freezeOperator(address toBeFrozen) external;

  /**
   * @notice Removes the 'frozen' status from each of the `frozenAddresses`
   * @dev Callable only by the contract owner (i.e. governance).
   */
  function resetFrozenStatus(address[] calldata frozenAddresses) external;

  /**
   * @notice this function is a called by middlewares during an operator's registration to make sure the operator's stake at registration
   *         is slashable until serveUntil
   * @param operator the operator whose stake update is being recorded
   * @param serveUntilTimestamp the timestamp until which the operator's stake at the current timestamp is slashable
   * @dev adds the middleware's slashing contract to the operator's linked list
   */
  function recordFirstStakeUpdate(address operator, uint32 serveUntilTimestamp) external;

  /**
   * @notice this function is a called by middlewares during a stake update for an operator (perhaps to free pending withdrawals)
   *         to make sure the operator's stake at updateTimestamp is slashable until serveUntil
   * @param operator the operator whose stake update is being recorded
   * @param updateTimestamp the timestamp for which the stake update is being recorded
   * @param serveUntilTimestamp the timestamp until which the operator's stake at updateTimestamp is slashable
   * @param insertAfter the element of the operators linked list that the currently updating middleware should be inserted after
   * @dev insertAfter should be calculated offchain before making the transaction that calls this. this is subject to race conditions,
   *      but it is anticipated to be rare and not detrimental.
   */
  function recordStakeUpdate(address operator, uint32 updateTimestamp, uint32 serveUntilTimestamp, uint256 insertAfter) external;

  /**
   * @notice this function is a called by middlewares during an operator's deregistration to make sure the operator's stake at deregistration
   *         is slashable until serveUntil
   * @param operator the operator whose stake update is being recorded
   * @param serveUntilTimestamp the timestamp until which the operator's stake at the current timestamp is slashable
   * @dev removes the middleware's slashing contract to the operator's linked list and revokes the middleware's (i.e. caller's) ability to
   * slash `operator` once `serveUntil` is reached
   */
  function recordLastStakeUpdateAndRevokeSlashingAbility(address operator, uint32 serveUntilTimestamp) external;

  /// @notice The PoolController.sol contract of BinLayer
  function poolController() external view returns (IPoolController);

  /// @notice The DelegationController.sol contract of BinLayer
  function delegation() external view returns (IDelegationController);

  /**
   * @notice Used to determine whether `staker` is actively 'frozen'. If a staker is frozen, then they are potentially subject to
   * slashing of their funds, and cannot cannot deposit or withdraw from the poolController until the slashing process is completed
   * and the staker's status is reset (to 'unfrozen').
   * @param staker The staker of interest.
   * @return Returns 'true' if `staker` themselves has their status set to frozen, OR if the staker is delegated
   * to an operator who has their status set to frozen. Otherwise returns 'false'.
   */
  function isFrozen(address staker) external view returns (bool);

  /// @notice Returns true if `slashingContract` is currently allowed to slash `toBeSlashed`.
  function canSlash(address toBeSlashed, address slashingContract) external view returns (bool);

  /// @notice Returns the timestamp until which `serviceContract` is allowed to slash the `operator`.
  function contractCanSlashOperatorUntilTimestamp(address operator, address serviceContract) external view returns (uint32);

  /// @notice Returns the timestamp at which the `serviceContract` last updated its view of the `operator`'s stake
  function latestUpdateTimestamp(address operator, address serviceContract) external view returns (uint32);

  /// @notice A search routine for finding the correct input value of `insertAfter` to `recordStakeUpdate` / `_updateMiddlewareList`.
  function getCorrectValueForInsertAfter(address operator, uint32 updateTimestamp) external view returns (uint256);

  /**
   * @notice Returns 'true' if `operator` can currently complete a withdrawal started at the `withdrawalStartTimestamp`, with `middlewareTimesIndex` used
   * to specify the index of a `MiddlewareTimes` struct in the operator's list (i.e. an index in `operatorToMiddlewareTimes[operator]`). The specified
   * struct is consulted as proof of the `operator`'s ability (or lack thereof) to complete the withdrawal.
   * This function will return 'false' if the operator cannot currently complete a withdrawal started at the `withdrawalStartTimestamp`, *or* in the event
   * that an incorrect `middlewareTimesIndex` is supplied, even if one or more correct inputs exist.
   * @param operator Either the operator who queued the withdrawal themselves, or if the withdrawing party is a staker who delegated to an operator,
   * this address is the operator *who the staker was delegated to* at the time of the `withdrawalStartTimestamp`.
   * @param withdrawalStartTimestamp The timestamp at which the withdrawal was initiated.
   * @param middlewareTimesIndex Indicates an index in `operatorToMiddlewareTimes[operator]` to consult as proof of the `operator`'s ability to withdraw
   * @dev The correct `middlewareTimesIndex` input should be computable off-chain.
   */
  function canWithdraw(address operator, uint32 withdrawalStartTimestamp, uint256 middlewareTimesIndex) external returns (bool);

  /**
   * operator =>
   *  [
   *      (
   *          the least recent update timestamp of all of the middlewares it's serving/served,
   *          latest time that the stake bonded at that update needed to serve until
   *      )
   *  ]
   */
  function operatorToMiddlewareTimes(address operator, uint256 arrayIndex) external view returns (MiddlewareTimes memory);

  /// @notice Getter function for fetching `operatorToMiddlewareTimes[operator].length`
  function middlewareTimesLength(address operator) external view returns (uint256);

  /// @notice Getter function for fetching `operatorToMiddlewareTimes[operator][index].stalestUpdateTimestamp`.
  function getMiddlewareTimesIndexStalestUpdateTimestamp(address operator, uint32 index) external view returns (uint32);

  /// @notice Getter function for fetching `operatorToMiddlewareTimes[operator][index].latestServeUntil`.
  function getMiddlewareTimesIndexServeUntilTimestamp(address operator, uint32 index) external view returns (uint32);

  /// @notice Getter function for fetching `_operatorToWhitelistedContractsByUpdate[operator].size`.
  function operatorWhitelistedContractsLinkedListSize(address operator) external view returns (uint256);

  /// @notice Getter function for fetching a single node in the operator's linked list (`_operatorToWhitelistedContractsByUpdate[operator]`).
  function operatorWhitelistedContractsLinkedListEntry(address operator, address node) external view returns (bool, uint256, uint256);
}
