contracts/core/AVSDirectory.sol.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import '@openzeppelin-upgrades/contracts/proxy/utils/Initializable.sol';
import '@openzeppelin-upgrades/contracts/access/OwnableUpgradeable.sol';
import '@openzeppelin-upgrades/contracts/security/ReentrancyGuardUpgradeable.sol';
import '../permissions/Pausable.sol';
import '../libraries/EIP1271SignatureUtils.sol';
import './AVSDirectoryStorage.sol';
import '../helpers/Errors.sol';

contract AVSDirectory is Initializable, OwnableUpgradeable, Pausable, AVSDirectoryStorage, ReentrancyGuardUpgradeable {
  // @dev Index for flag that pauses operator register/deregister to avs when set.
  uint8 internal constant PAUSED_OPERATOR_REGISTER_DEREGISTER_TO_AVS = 0;

  // @dev Chain ID at the time of contract deployment
  uint256 internal immutable ORIGINAL_CHAIN_ID;

  /*******************************************************************************
                            INITIALIZING FUNCTIONS
    *******************************************************************************/

  /**
   * @dev Initializes the immutable addresses of the delegation controller contracts
   */
  constructor(IDelegationController _delegation) AVSDirectoryStorage(_delegation) {
    _disableInitializers();
    ORIGINAL_CHAIN_ID = block.chainid;
  }

  /**
   * @dev Initializes the addresses of the initial owner, pauser registry, and paused status.
   * minWithdrawalDelayBlocks is set only once here
   */
  function initialize(address initialOwner, IPauserRegistry _pauserRegistry, uint256 initialPausedStatus) external initializer {
    _initializePauser(_pauserRegistry, initialPausedStatus);
    _DOMAIN_SEPARATOR = _calculateDomainSeparator();
    _transferOwnership(initialOwner);
  }

  /*******************************************************************************
                            EXTERNAL FUNCTIONS 
    *******************************************************************************/

  /**
   * @notice Called by the AVS's service manager contract to register an operator with the avs.
   * @param operator The address of the operator to register.
   * @param operatorSignature The signature, salt, and expiry of the operator's signature.
   */
  function registerOperatorToAVS(
    address operator,
    ISignatureUtils.SignatureWithSaltAndExpiry memory operatorSignature
  ) external onlyWhenNotPaused(PAUSED_OPERATOR_REGISTER_DEREGISTER_TO_AVS) {
    require(operatorSignature.expiry >= block.timestamp, Errors.SIGNATURE_EXPIRED);
    require(avsOperatorStatus[msg.sender][operator] != OperatorAVSRegistrationStatus.REGISTERED, Errors.OPERATOR_ALREADY_REGISTERED);
    require(!operatorSaltIsSpent[operator][operatorSignature.salt], Errors.SALT_ALREADY_SPENT);
    require(delegation.isOperator(operator), Errors.NOT_REGISTERED_IN_BINLAYER);

    // Calculate the digest hash
    bytes32 operatorRegistrationDigestHash = calculateOperatorAVSRegistrationDigestHash({
      operator: operator,
      avs: msg.sender,
      salt: operatorSignature.salt,
      expiry: operatorSignature.expiry
    });

    // forgefmt: disable-next-item
    // Check that the signature is valid
    EIP1271SignatureUtils.checkSignature_EIP1271(operator, operatorRegistrationDigestHash, operatorSignature.signature);

    // Set the operator as registered
    avsOperatorStatus[msg.sender][operator] = OperatorAVSRegistrationStatus.REGISTERED;

    // Mark the salt as spent
    operatorSaltIsSpent[operator][operatorSignature.salt] = true;

    emit OperatorAVSRegistrationStatusUpdated(operator, msg.sender, OperatorAVSRegistrationStatus.REGISTERED);
  }

  /**
   * @notice Called by an avs to deregister an operator with the avs.
   * @param operator The address of the operator to deregister.
   */
  function deregisterOperatorFromAVS(address operator) external onlyWhenNotPaused(PAUSED_OPERATOR_REGISTER_DEREGISTER_TO_AVS) {
    require(avsOperatorStatus[msg.sender][operator] == OperatorAVSRegistrationStatus.REGISTERED, Errors.NOT_REGISTERED_IN_BINLAYER);

    // Set the operator as deregistered
    avsOperatorStatus[msg.sender][operator] = OperatorAVSRegistrationStatus.UNREGISTERED;

    emit OperatorAVSRegistrationStatusUpdated(operator, msg.sender, OperatorAVSRegistrationStatus.UNREGISTERED);
  }

  /**
   * @notice Called by an avs to emit an `AVSMetadataURIUpdated` event indicating the information has updated.
   * @param metadataURI The URI for metadata associated with an avs
   */
  function updateAVSMetadataURI(string calldata metadataURI) external {
    emit AVSMetadataURIUpdated(msg.sender, metadataURI);
  }

  /**
   * @notice Called by an operator to cancel a salt that has been used to register with an AVS.
   * @param salt A unique and single use value associated with the approver signature.
   */
  function cancelSalt(bytes32 salt) external {
    require(!operatorSaltIsSpent[msg.sender][salt], Errors.CANNOT_CANCEL_SPENT_SALT);
    operatorSaltIsSpent[msg.sender][salt] = true;
  }

  /*******************************************************************************
                            VIEW FUNCTIONS
    *******************************************************************************/

  /**
   * @notice Calculates the digest hash to be signed by an operator to register with an AVS
   * @param operator The account registering as an operator
   * @param avs The address of the service manager contract for the AVS that the operator is registering to
   * @param salt A unique and single use value associated with the approver signature.
   * @param expiry Time after which the approver's signature becomes invalid
   */
  function calculateOperatorAVSRegistrationDigestHash(
    address operator,
    address avs,
    bytes32 salt,
    uint256 expiry
  ) public view returns (bytes32) {
    // calculate the struct hash
    bytes32 structHash = keccak256(abi.encode(OPERATOR_AVS_REGISTRATION_TYPEHASH, operator, avs, salt, expiry));
    // calculate the digest hash
    bytes32 digestHash = keccak256(abi.encodePacked('\x19\x01', domainSeparator(), structHash));
    return digestHash;
  }

  /**
   * @notice Getter function for the current EIP-712 domain separator for this contract.
   * @dev The domain separator will change in the event of a fork that changes the ChainID.
   */
  function domainSeparator() public view returns (bytes32) {
    if (block.chainid == ORIGINAL_CHAIN_ID) {
      return _DOMAIN_SEPARATOR;
    } else {
      return _calculateDomainSeparator();
    }
  }

  // @notice Internal function for calculating the current domain separator of this contract
  function _calculateDomainSeparator() internal view returns (bytes32) {
    return keccak256(abi.encode(DOMAIN_TYPEHASH, keccak256(bytes('BinLayer')), block.chainid, address(this)));
  }
}

contracts/core/AVSDirectoryStorage.sol.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import '../interfaces/IAVSDirectory.sol';
import '../interfaces/IPoolController.sol';
import '../interfaces/IDelegationController.sol';
import '../interfaces/ISlasher.sol';

abstract contract AVSDirectoryStorage is IAVSDirectory {
  /// @notice The EIP-712 typehash for the contract's domain
  bytes32 public constant DOMAIN_TYPEHASH = keccak256('EIP712Domain(string name,uint256 chainId,address verifyingContract)');

  /// @notice The EIP-712 typehash for the `Registration` struct used by the contract
  bytes32 public constant OPERATOR_AVS_REGISTRATION_TYPEHASH =
    keccak256('OperatorAVSRegistration(address operator,address avs,bytes32 salt,uint256 expiry)');

  /// @notice The DelegationController contract for BinLayer
  IDelegationController public immutable delegation;

  /**
   * @notice Original EIP-712 Domain separator for this contract.
   * @dev The domain separator may change in the event of a fork that modifies the ChainID.
   * Use the getter function `domainSeparator` to get the current domain separator for this contract.
   */
  bytes32 internal _DOMAIN_SEPARATOR;

  /// @notice Mapping: AVS => operator => enum of operator status to the AVS
  mapping(address => mapping(address => OperatorAVSRegistrationStatus)) public avsOperatorStatus;

  /// @notice Mapping: operator => 32-byte salt => whether or not the salt has already been used by the operator.
  /// @dev Salt is used in the `registerOperatorToAVS` function.
  mapping(address => mapping(bytes32 => bool)) public operatorSaltIsSpent;

  constructor(IDelegationController _delegation) {
    delegation = _delegation;
  }

  /**
   * @dev This empty reserved space is put in place to allow future versions to add new
   * variables without shifting down storage in the inheritance chain.
   * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
   */
  uint256[49] private __gap;
}

contracts/core/DelegationController.sol.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import '@openzeppelin-upgrades/contracts/proxy/utils/Initializable.sol';
import '@openzeppelin-upgrades/contracts/access/OwnableUpgradeable.sol';
import '@openzeppelin-upgrades/contracts/security/ReentrancyGuardUpgradeable.sol';
import '../permissions/Pausable.sol';
import '../libraries/EIP1271SignatureUtils.sol';
import './DelegationControllerStorage.sol';
import '../helpers/Errors.sol';

/**
 * @title DelegationController.sol
 * @notice  this is the delegation contract for BinLayer. The primary functions of this contract are:
 * - allowing anyone to register as an operator in BinLayer
 * - enabling operators to set parameters for stakers who delegate to them
 * - allowing any staker to delegate their stake to the operator of their choice (each staker can only delegate to one operator at a time)
 * - allowing a staker to undelegate their assets from the operator they are delegated to (done as part of the withdrawal process, initiated through the PoolController)
 */
contract DelegationController is Initializable, OwnableUpgradeable, Pausable, DelegationControllerStorage, ReentrancyGuardUpgradeable {
  // @dev Flag index that, when set, pauses new delegations
  uint8 internal constant PAUSED_NEW_DELEGATION = 0;

  // @dev Index for flag that pauses unstake when set.
  uint8 internal constant PAUSED_UNSTAKE = 1;

  // @dev Index for flag that pauses withdraw when set.
  uint8 internal constant PAUSED_WITHDRAW = 2;

  // @dev Chain ID at the time of contract deployment
  uint256 internal immutable ORIGINAL_CHAIN_ID;

  // @dev Maximum Value for `stakerOptOutWindow`. Approximately equivalent to 6 months.
  uint256 public constant MAX_STAKER_OPT_OUT_WINDOW = 180 days;

  // @notice Simple permission for functions that are only callable by the PoolController.sol contract
  modifier onlyPoolController() {
    require(msg.sender == address(poolController), Errors.ONLY_POOL_CONTROLLER);
    _;
  }

  /*******************************************************************************
                            INITIALIZING FUNCTIONS
    *******************************************************************************/

  /**
   * @dev Initializes the immutable addresses of the pool mananger and slasher.
   */
  constructor(IPoolController _poolController, ISlasher _slasher) DelegationControllerStorage(_poolController, _slasher) {
    _disableInitializers();
    ORIGINAL_CHAIN_ID = block.chainid;
  }

  /**
   * @dev Initializes the addresses of the initial owner, pauser registry, and paused status.
   * minWithdrawalDelay is set only once here
   */
  function initialize(
    address initialOwner,
    IPauserRegistry _pauserRegistry,
    uint256 initialPausedStatus,
    uint256 _minWithdrawalDelay,
    IPool[] calldata _pools,
    uint256[] calldata _withdrawalDelay
  ) external initializer {
    _initializePauser(_pauserRegistry, initialPausedStatus);
    _DOMAIN_SEPARATOR = _calculateDomainSeparator();
    __ReentrancyGuard_init();
    _transferOwnership(initialOwner);
    _setMinWithdrawalDelay(_minWithdrawalDelay);
    _setPoolWithdrawalDelay(_pools, _withdrawalDelay);
  }

  /*******************************************************************************
                            EXTERNAL FUNCTIONS 
    *******************************************************************************/

  /**
   * @notice Registers the caller as an operator in BinLayer.
   * @param registeringOperatorDetails is the `OperatorDetails` for the operator.
   * @param metadataURI is a URI for the operator's metadata, i.e. a link providing more details on the operator.
   *
   * @dev Once an operator is registered, they cannot 'deregister' as an operator, and they will forever be considered "delegated to themself".
   * @dev This function will revert if the caller is already delegated to an operator.
   * @dev Note that the `metadataURI` is *never stored * and is only emitted in the `OperatorMetadataURIUpdated` event
   */
  function registerAsOperator(OperatorDetails calldata registeringOperatorDetails, string calldata metadataURI) external {
    require(!isDelegated(msg.sender), Errors.OPERATOR_ALREADY_REGISTERED);
    _setOperatorDetails(msg.sender, registeringOperatorDetails);
    SignatureWithExpiry memory emptySignatureAndExpiry;
    // delegate from the operator to themselves
    _delegate(msg.sender, msg.sender, emptySignatureAndExpiry, bytes32(0));
    // emit events
    emit OperatorRegistered(msg.sender, registeringOperatorDetails);
    emit OperatorMetadataURIUpdated(msg.sender, metadataURI);
  }

  /**
   * @notice Updates an operator's stored `OperatorDetails`.
   * @param newOperatorDetails is the updated `OperatorDetails` for the operator, to replace their current OperatorDetails`.
   *
   * @dev The caller must have previously registered as an operator in BinLayer.
   */
  function modifyOperatorDetails(OperatorDetails calldata newOperatorDetails) external {
    require(isOperator(msg.sender), Errors.CALLER_NOT_OPERATOR);
    _setOperatorDetails(msg.sender, newOperatorDetails);
  }

  /**
   * @notice Called by an operator to emit an `OperatorMetadataURIUpdated` event indicating the information has updated.
   * @param metadataURI The URI for metadata associated with an operator
   */
  function updateOperatorMetadataURI(string calldata metadataURI) external {
    require(isOperator(msg.sender), Errors.CALLER_NOT_OPERATOR);
    emit OperatorMetadataURIUpdated(msg.sender, metadataURI);
  }

  /**
   * @notice Caller delegates their stake to an operator.
   * @param operator The account (`msg.sender`) is delegating its assets to for use in serving applications built on BinLayer.
   * @param approverSignatureAndExpiry Verifies the operator approves of this delegation
   * @param approverSalt A unique single use value tied to an individual signature.
   * @dev The approverSignatureAndExpiry is used in the event that:
   *          1) the operator's `delegationApprover` address is set to a non-zero value.
   *                  AND
   *          2) neither the operator nor their `delegationApprover` is the `msg.sender`, since in the event that the operator
   *             or their delegationApprover is the `msg.sender`, then approval is assumed.
   * @dev In the event that `approverSignatureAndExpiry` is not checked, its content is ignored entirely; it's recommended to use an empty input
   * in this case to save on complexity + gas costs
   */
  function delegateTo(address operator, SignatureWithExpiry memory approverSignatureAndExpiry, bytes32 approverSalt) external {
    require(!isDelegated(msg.sender), Errors.ALREADY_DELEGATED);
    require(isOperator(operator), Errors.NOT_REGISTERED_IN_BINLAYER);
    // go through the internal delegation flow, checking the `approverSignatureAndExpiry` if applicable
    _delegate(msg.sender, operator, approverSignatureAndExpiry, approverSalt);
  }

  /**
   * @notice Caller delegates a staker's stake to an operator with valid signatures from both parties.
   * @param staker The account delegating stake to an `operator` account
   * @param operator The account (`staker`) is delegating its assets to for use in serving applications built on BinLayer.
   * @param stakerSignatureAndExpiry Signed data from the staker authorizing delegating stake to an operator
   * @param approverSignatureAndExpiry is a parameter that will be used for verifying that the operator approves of this delegation action in the event that:
   * @param approverSalt Is a salt used to help guarantee signature uniqueness. Each salt can only be used once by a given approver.
   *
   * @dev If `staker` is an EOA, then `stakerSignature` is verified to be a valid ECDSA stakerSignature from `staker`, indicating their intention for this action.
   * @dev If `staker` is a contract, then `stakerSignature` will be checked according to EIP-1271.
   * @dev the operator's `delegationApprover` address is set to a non-zero value.
   * @dev neither the operator nor their `delegationApprover` is the `msg.sender`, since in the event that the operator or their delegationApprover
   * is the `msg.sender`, then approval is assumed.
   * @dev This function will revert if the current `block.timestamp` is equal to or exceeds the expiry
   * @dev In the case that `approverSignatureAndExpiry` is not checked, its content is ignored entirely; it's recommended to use an empty input
   * in this case to save on complexity + gas costs
   */
  function delegateToBySignature(
    address staker,
    address operator,
    SignatureWithExpiry memory stakerSignatureAndExpiry,
    SignatureWithExpiry memory approverSignatureAndExpiry,
    bytes32 approverSalt
  ) external {
    // check the signature expiry
    require(stakerSignatureAndExpiry.expiry >= block.timestamp, Errors.SIGNATURE_EXPIRED);

    require(!isDelegated(staker), Errors.ALREADY_DELEGATED);
    require(isOperator(operator), Errors.NOT_REGISTERED_IN_BINLAYER);

    // calculate the digest hash, then increment `staker`'s nonce
    uint256 currentStakerNonce = stakerNonce[staker];
    bytes32 stakerDigestHash = calculateStakerDelegationDigestHash(staker, currentStakerNonce, operator, stakerSignatureAndExpiry.expiry);
    unchecked {
      stakerNonce[staker] = currentStakerNonce + 1;
    }

    // actually check that the signature is valid
    EIP1271SignatureUtils.checkSignature_EIP1271(staker, stakerDigestHash, stakerSignatureAndExpiry.signature);

    // go through the internal delegation flow, checking the `approverSignatureAndExpiry` if applicable
    _delegate(staker, operator, approverSignatureAndExpiry, approverSalt);
  }

  /**
   * Allows the staker, the staker's operator, or that operator's delegationApprover to undelegate
   * a staker from their operator. Undelegation immediately removes ALL active shares/pools from
   * both the staker and operator, and places the shares and pools in the unstake
   */
  function undelegate(address staker) external onlyWhenNotPaused(PAUSED_UNSTAKE) returns (bytes32[] memory withdrawalRoots) {
    require(isDelegated(staker), Errors.STAKER_MUST_BE_DELEGATED);
    require(!isOperator(staker), Errors.CANNOT_UNDELEGATE_OPERATOR);
    require(staker != address(0), Errors.ZERO_ADDRESS_NOT_VALID);
    address operator = delegatedTo[staker];
    require(
      msg.sender == staker || msg.sender == operator || msg.sender == _operatorDetails[operator].delegationApprover,
      Errors.CALLER_CANNOT_UNDELEGATE
    );

    // Gather pools and shares to remove from staker/operator during undelegation
    // Undelegation removes ALL currently-active pools and shares
    (IPool[] memory pools, uint256[] memory shares) = getDelegatableShares(staker);

    // emit an event if this action was not initiated by the staker themselves
    if (msg.sender != staker) {
      emit StakerForceUndelegated(staker, operator);
    }

    // undelegate the staker
    emit StakerUndelegated(staker, operator);
    delegatedTo[staker] = address(0);

    // if no delegatable shares, return an empty array, and don't unstake
    if (pools.length == 0) {
      withdrawalRoots = new bytes32[](0);
    } else {
      withdrawalRoots = new bytes32[](pools.length);
      for (uint256 i = 0; i < pools.length; i++) {
        IPool[] memory singlePool = new IPool[](1);
        uint256[] memory singleShare = new uint256[](1);
        singlePool[0] = pools[i];
        singleShare[0] = shares[i];

        withdrawalRoots[i] = _removeSharesAndUnstake({
          staker: staker,
          operator: operator,
          withdrawer: staker,
          pools: singlePool,
          shares: singleShare
        });
      }
    }

    return withdrawalRoots;
  }

  /**
   * Enables a staker to withdraw a portion of shares. Withdrawn shares/pools are promptly removed.
   * from the staker. If the staker is delegated, withdrawn shares/pools are also removed from
   * their operator.
   *
   * All withdrawn shares/pools are placed in a queue and can be fully withdrawn after a delay.
   */
  function unstakes(UnstakeParams[] calldata unstakeParams) external onlyWhenNotPaused(PAUSED_UNSTAKE) returns (bytes32[] memory) {
    bytes32[] memory withdrawalRoots = new bytes32[](unstakeParams.length);
    address operator = delegatedTo[msg.sender];

    for (uint256 i = 0; i < unstakeParams.length; i++) {
      require(unstakeParams[i].pools.length == unstakeParams[i].shares.length, Errors.INPUT_LENGTH_MISMATCH);
      require(
        unstakeParams[i].withdrawer == msg.sender || unstakeParams[i].withdrawer == wrappedTokenGateway,
        Errors.WITHDRAWER_NOT_STAKER_OR_GATEWAY
      );

      // Remove shares from staker's pools and place pools/shares in unstake.
      // If the staker is delegated to an operator, the operator's delegated shares are also reduced
      // NOTE: This will fail if the staker doesn't have the shares implied by the input parameters
      withdrawalRoots[i] = _removeSharesAndUnstake({
        staker: msg.sender,
        operator: operator,
        withdrawer: unstakeParams[i].withdrawer,
        pools: unstakeParams[i].pools,
        shares: unstakeParams[i].shares
      });
    }
    return withdrawalRoots;
  }

  /**
   * @notice Used to complete the specified `withdrawal`. The caller must match `withdrawal.withdrawer`
   * @param withdrawal The Withdrawal to complete.
   * @param tokens Array in which the i-th entry specifies the `token` input to the 'withdraw' function of the i-th Pool in the `withdrawal.pools` array.
   * This input can be provided with zero length if `receiveAsTokens` is set to 'false' (since in that case, this input will be unused)
   * @param middlewareTimesIndex is the index in the operator that the staker who triggered the withdrawal was delegated to's middleware times array
   * @param receiveAsTokens If true, the shares specified in the withdrawal will be withdrawn from the specified pools themselves
   * and sent to the caller, through calls to `withdrawal.pools[i].withdraw`. If false, then the shares in the specified pools
   * will simply be transferred to the caller directly.
   * @dev middlewareTimesIndex is unused, but will be used in the Slasher eventually
   * @dev beaconChainETHPool shares are non-transferrable, so if `receiveAsTokens = false` and `withdrawal.withdrawer != withdrawal.staker`, note that
   * any beaconChainETHPool shares in the `withdrawal` will be _returned to the staker_, rather than transferred to the withdrawer, unlike shares in
   * any other pools, which will be transferred to the withdrawer.
   */
  function withdraw(
    Withdrawal calldata withdrawal,
    IERC20[] calldata tokens,
    uint256 middlewareTimesIndex,
    bool receiveAsTokens
  ) external onlyWhenNotPaused(PAUSED_WITHDRAW) nonReentrant {
    _withdraw(withdrawal, tokens, middlewareTimesIndex, receiveAsTokens);
  }

  /**
   * @notice Array-ified version of `withdraw`.
   * Used to complete the specified `withdrawals`. The function caller must match `withdrawals[...].withdrawer`
   * @param withdrawals The Withdrawals to complete.
   * @param tokens Array of tokens for each Withdrawal. See `withdraw` for the usage of a single array.
   * @param middlewareTimesIndexes One index to reference per Withdrawal. See `withdraw` for the usage of a single index.
   * @param receiveAsTokens Whether or not to complete each withdrawal as tokens. See `withdraw` for the usage of a single boolean.
   * @dev See `withdraw` for relevant dev tags
   */
  function withdraws(
    Withdrawal[] calldata withdrawals,
    IERC20[][] calldata tokens,
    uint256[] calldata middlewareTimesIndexes,
    bool[] calldata receiveAsTokens
  ) external onlyWhenNotPaused(PAUSED_WITHDRAW) nonReentrant {
    for (uint256 i = 0; i < withdrawals.length; ++i) {
      _withdraw(withdrawals[i], tokens[i], middlewareTimesIndexes[i], receiveAsTokens[i]);
    }
  }

  /**
   * @notice Increases a staker's delegated share balance in a pool.
   * @param staker The address to increase the delegated shares for their operator.
   * @param pool The pool in which to increase the delegated shares.
   * @param shares The number of shares to increase.
   *
   * @dev *If the staker is actively delegated*, then increases the `staker`'s delegated shares in `pool` by `shares`. Otherwise does nothing.
   * @dev Callable only by the PoolController.sol.
   */
  function increaseDelegatedShares(address staker, IPool pool, uint256 shares) external onlyPoolController {
    // if the staker is delegated to an operator
    if (isDelegated(staker)) {
      address operator = delegatedTo[staker];

      // add pool shares to delegate's shares
      _increaseOperatorShares({operator: operator, staker: staker, pool: pool, shares: shares});
    }
  }

  /**
   * @notice Decreases a staker's delegated share balance in a pool.
   * @param staker The address to increase the delegated shares for their operator.
   * @param pool The pool in which to decrease the delegated shares.
   * @param shares The number of shares to decrease.
   *
   * @dev *If the staker is actively delegated*, then decreases the `staker`'s delegated shares in `pool` by `shares`. Otherwise does nothing.
   * @dev Callable only by the PoolController.sol.
   */
  function decreaseDelegatedShares(address staker, IPool pool, uint256 shares) external onlyPoolController {
    // if the staker is delegated to an operator
    if (isDelegated(staker)) {
      address operator = delegatedTo[staker];

      // subtract pool shares from delegate's shares
      _decreaseOperatorShares({operator: operator, staker: staker, pool: pool, shares: shares});
    }
  }

  /**
   * @notice Owner-only function for modifying the value of the `minWithdrawalDelay` variable.
   * @param newMinWithdrawalDelay new value of `minWithdrawalDelay`.
   */
  function setMinWithdrawalDelay(uint256 newMinWithdrawalDelay) external onlyOwner {
    _setMinWithdrawalDelay(newMinWithdrawalDelay);
  }

  /**
   * @notice Called by owner to set the minimum withdrawal delay for each passed in pool
   * Note that the min cooldown to complete a withdrawal of a pool is
   * MAX(minWithdrawalDelay, poolWithdrawalDelay[pool])
   * @param pools The pools to set the minimum withdrawal delay for
   * @param withdrawalDelay The minimum withdrawal delay to set for each pool
   */
  function setPoolWithdrawalDelay(IPool[] calldata pools, uint256[] calldata withdrawalDelay) external onlyOwner {
    _setPoolWithdrawalDelay(pools, withdrawalDelay);
  }

  /**
   * @notice Called by owner to update the wrapped token gateway
   * @param _newWrappedTokenGateway New wrapped token gateway address
   */
  function updateWrappedTokenGateway(address _newWrappedTokenGateway) external onlyOwner {
    emit UpdateWrappedTokenGateway(wrappedTokenGateway, _newWrappedTokenGateway);
    wrappedTokenGateway = _newWrappedTokenGateway;
  }

  /*******************************************************************************
                            INTERNAL FUNCTIONS
    *******************************************************************************/

  /**
   * @notice Sets operator parameters in the `_operatorDetails` mapping.
   * @param operator The account registered as an operator updating their operatorDetails
   * @param newOperatorDetails The new parameters for the operator
   */
  function _setOperatorDetails(address operator, OperatorDetails calldata newOperatorDetails) internal {
    require(newOperatorDetails.stakerOptOutWindow <= MAX_STAKER_OPT_OUT_WINDOW, Errors.OPT_OUT_WINDOW_EXCEEDS_MAX);
    require(newOperatorDetails.stakerOptOutWindow >= _operatorDetails[operator].stakerOptOutWindow, Errors.DECREASE_OPT_OUT_WINDOW);
    _operatorDetails[operator] = newOperatorDetails;
    emit OperatorDetailsModified(msg.sender, newOperatorDetails);
  }

  /**
   * @notice Delegates *from* a `staker` *to* an `operator`.
   * @param staker The address to delegate *from* -- this address is delegating control of its own assets.
   * @param operator The address to delegate *to* -- this address is being given power to place the `staker`'s assets at risk on services
   * @param approverSignatureAndExpiry Verifies the operator approves of this delegation
   * @param approverSalt Is a salt used to help guarantee signature uniqueness. Each salt can only be used once by a given approver.
   * @dev Ensures that:
   *          1) the `staker` is not already delegated to an operator
   *          2) the `operator` has indeed registered as an operator in BinLayer
   *          3) if applicable, that the approver signature is valid and non-expired
   */
  function _delegate(
    address staker,
    address operator,
    SignatureWithExpiry memory approverSignatureAndExpiry,
    bytes32 approverSalt
  ) internal onlyWhenNotPaused(PAUSED_NEW_DELEGATION) {
    // fetch the operator's `delegationApprover` address and store it in memory in case we need to use it multiple times
    address _delegationApprover = _operatorDetails[operator].delegationApprover;
    /**
     * Check the `_delegationApprover`'s signature, if applicable.
     * If the `_delegationApprover` is the zero address, then the operator allows all stakers to delegate to them and this verification is skipped.
     * If the `_delegationApprover` or the `operator` themselves is the caller, then approval is assumed and signature verification is skipped as well.
     */
    if (_delegationApprover != address(0) && msg.sender != _delegationApprover && msg.sender != operator) {
      // check the signature expiry
      require(approverSignatureAndExpiry.expiry >= block.timestamp, Errors.SIGNATURE_EXPIRED);
      // check that the salt hasn't been used previously, then mark the salt as spent
      require(!delegationApproverSaltIsSpent[_delegationApprover][approverSalt], Errors.SALT_ALREADY_SPENT);
      delegationApproverSaltIsSpent[_delegationApprover][approverSalt] = true;

      // calculate the digest hash
      bytes32 approverDigestHash = calculateDelegationApprovalDigestHash(
        staker,
        operator,
        _delegationApprover,
        approverSalt,
        approverSignatureAndExpiry.expiry
      );

      // actually check that the signature is valid
      EIP1271SignatureUtils.checkSignature_EIP1271(_delegationApprover, approverDigestHash, approverSignatureAndExpiry.signature);
    }

    // record the delegation relation between the staker and operator, and emit an event
    delegatedTo[staker] = operator;
    emit StakerDelegated(staker, operator);

    (IPool[] memory pools, uint256[] memory shares) = getDelegatableShares(staker);

    for (uint256 i = 0; i < pools.length; ) {
      _increaseOperatorShares({operator: operator, staker: staker, pool: pools[i], shares: shares[i]});

      unchecked {
        ++i;
      }
    }
  }

  /**
   * @dev commented-out param (middlewareTimesIndex) is the index in the operator that the staker who triggered the withdrawal was delegated to's middleware times array
   * This param is intended to be passed on to the Slasher contract.
   */
  function _withdraw(
    Withdrawal calldata withdrawal,
    IERC20[] calldata tokens,
    uint256 /*middlewareTimesIndex*/,
    bool receiveAsTokens
  ) internal {
    bytes32 withdrawalRoot = calculateWithdrawalRoot(withdrawal);

    require(pendingWithdrawals[withdrawalRoot], Errors.ACTION_NOT_IN_QUEUE);

    require(withdrawal.startTimestamp + minWithdrawalDelay <= block.timestamp, Errors.MIN_WITHDRAWAL_DELAY_NOT_PASSED);

    require(msg.sender == withdrawal.withdrawer, Errors.ONLY_WITHDRAWER_CAN_COMPLETE);

    if (receiveAsTokens) {
      require(tokens.length == withdrawal.pools.length, Errors.INPUT_LENGTH_MISMATCH);
    }

    // Remove `withdrawalRoot` from pending roots
    delete pendingWithdrawals[withdrawalRoot];

    // Finalize action by converting shares to tokens for each pool, or
    // by re-awarding shares in each pool.
    if (receiveAsTokens) {
      for (uint256 i = 0; i < withdrawal.pools.length; ) {
        require(
          withdrawal.startTimestamp + poolWithdrawalDelay[withdrawal.pools[i]] <= block.timestamp,
          Errors.WITHDRAWAL_DELAY_NOT_PASSED
        );

        _withdrawSharesAsTokens({
          staker: withdrawal.staker,
          withdrawer: msg.sender,
          pool: withdrawal.pools[i],
          shares: withdrawal.shares[i],
          token: tokens[i]
        });
        unchecked {
          ++i;
        }
      }
      // Award shares back in PoolController.sol. If withdrawer is delegated, increase the shares delegated to the operator
    } else {
      address currentOperator = delegatedTo[msg.sender];
      for (uint256 i = 0; i < withdrawal.pools.length; ) {
        require(
          withdrawal.startTimestamp + poolWithdrawalDelay[withdrawal.pools[i]] <= block.timestamp,
          Errors.WITHDRAWAL_DELAY_NOT_PASSED
        );

        poolController.addShares(msg.sender, tokens[i], withdrawal.pools[i], withdrawal.shares[i]);
        // Similar to `isDelegated` logic
        if (currentOperator != address(0)) {
          _increaseOperatorShares({
            operator: currentOperator,
            // the 'staker' here is the address receiving new shares
            staker: msg.sender,
            pool: withdrawal.pools[i],
            shares: withdrawal.shares[i]
          });
        }

        unchecked {
          ++i;
        }
      }
    }

    emit WithdrawalCompleted(withdrawalRoot);
  }

  // @notice Increases `operator`s delegated shares in `pool` by `shares` and emits an `OperatorSharesIncreased` event
  function _increaseOperatorShares(address operator, address staker, IPool pool, uint256 shares) internal {
    operatorShares[operator][pool] += shares;
    emit OperatorSharesIncreased(operator, staker, pool, shares);
  }

  // @notice Decreases `operator`s delegated shares in `pool` by `shares` and emits an `OperatorSharesDecreased` event
  function _decreaseOperatorShares(address operator, address staker, IPool pool, uint256 shares) internal {
    // This will revert on underflow, so no check needed
    operatorShares[operator][pool] -= shares;
    emit OperatorSharesDecreased(operator, staker, pool, shares);
  }

  /**
   * @notice Removes `shares` in `pools` from `staker` who is currently delegated to `operator` and unstake to the `withdrawer`.
   * @dev If the `operator` is indeed an operator, then the operator's delegated shares in the `pools` are also decreased appropriately.
   * @dev If `withdrawer` is not the same address as `staker`, then thirdPartyTransfersForbidden[pool] must be set to false in the PoolController.sol.
   */
  function _removeSharesAndUnstake(
    address staker,
    address operator,
    address withdrawer,
    IPool[] memory pools,
    uint256[] memory shares
  ) internal returns (bytes32) {
    require(staker != address(0), Errors.ZERO_ADDRESS_NOT_VALID);
    require(pools.length != 0, Errors.POOLS_CANNOT_BE_EMPTY);

    // Remove shares from staker and operator
    // Each of these operations fail if we attempt to remove more shares than exist
    for (uint256 i = 0; i < pools.length; ) {
      // Similar to `isDelegated` logic
      if (operator != address(0)) {
        _decreaseOperatorShares({operator: operator, staker: staker, pool: pools[i], shares: shares[i]});
      }

      require(staker == withdrawer || !poolController.thirdPartyTransfersForbidden(pools[i]), Errors.THIRD_PARTY_TRANSFERS_FORBIDDEN);
      // this call will revert if `shares[i]` exceeds the Staker's current shares in `pools[i]`
      poolController.removeShares(staker, pools[i], shares[i]);

      unchecked {
        ++i;
      }
    }

    // Create queue entry and increment withdrawal nonce
    uint256 nonce = cumulativeWithdrawalsQueued[staker];
    cumulativeWithdrawalsQueued[staker]++;

    Withdrawal memory withdrawal = Withdrawal({
      staker: staker,
      delegatedTo: operator,
      withdrawer: withdrawer,
      nonce: nonce,
      startTimestamp: uint32(block.timestamp),
      pools: pools,
      shares: shares
    });

    bytes32 withdrawalRoot = calculateWithdrawalRoot(withdrawal);

    // Place withdrawal in queue
    pendingWithdrawals[withdrawalRoot] = true;

    emit WithdrawalQueued(withdrawalRoot, withdrawal);
    return withdrawalRoot;
  }

  /**
   * @notice Withdraws `shares` in `pool` to `withdrawer`. Call is ultimately forwarded to the `pool` with info on the `token`.
   */
  function _withdrawSharesAsTokens(address staker, address withdrawer, IPool pool, uint256 shares, IERC20 token) internal {
    poolController.withdrawSharesAsTokens(withdrawer, pool, shares, token);
  }

  function _setMinWithdrawalDelay(uint256 _minWithdrawalDelay) internal {
    require(_minWithdrawalDelay <= MAX_WITHDRAWAL_DELAY, Errors.MIN_WITHDRAWAL_DELAY_EXCEEDS_MAX);
    emit MinWithdrawalDelaySet(minWithdrawalDelay, _minWithdrawalDelay);
    minWithdrawalDelay = _minWithdrawalDelay;
  }

  /**
   * @notice Sets the withdrawal delay for each pool in `_pools` to `_withdrawalDelay`.
   * gets called when initializing contract or by calling `setPoolWithdrawalDelay`
   */
  function _setPoolWithdrawalDelay(IPool[] calldata _pools, uint256[] calldata _withdrawalDelay) internal {
    require(_pools.length == _withdrawalDelay.length, Errors.INPUT_LENGTH_MISMATCH);
    uint256 numStrats = _pools.length;
    for (uint256 i = 0; i < numStrats; ++i) {
      IPool pool = _pools[i];
      uint256 prevPoolWithdrawalDelay = poolWithdrawalDelay[pool];
      uint256 newPoolWithdrawalDelay = _withdrawalDelay[i];
      require(newPoolWithdrawalDelay <= MAX_WITHDRAWAL_DELAY, Errors.POOL_WITHDRAWAL_DELAY_EXCEEDS_MAX);

      // set the new withdrawal delay
      poolWithdrawalDelay[pool] = newPoolWithdrawalDelay;
      emit PoolWithdrawalDelaySet(pool, prevPoolWithdrawalDelay, newPoolWithdrawalDelay);
    }
  }

  /*******************************************************************************
                            VIEW FUNCTIONS
    *******************************************************************************/

  /**
   * @notice Getter function for the current EIP-712 domain separator for this contract.
   *
   * @dev The domain separator will change in the event of a fork that changes the ChainID.
   * @dev By introducing a domain separator the DApp developers are guaranteed that there can be no signature collision.
   * for more detailed information please read EIP-712.
   */
  function domainSeparator() public view returns (bytes32) {
    if (block.chainid == ORIGINAL_CHAIN_ID) {
      return _DOMAIN_SEPARATOR;
    } else {
      return _calculateDomainSeparator();
    }
  }

  /**
   * @notice Returns 'true' if `staker` *is* actively delegated, and 'false' otherwise.
   */
  function isDelegated(address staker) public view returns (bool) {
    return (delegatedTo[staker] != address(0));
  }

  /**
   * @notice Returns true is an operator has previously registered for delegation.
   */
  function isOperator(address operator) public view returns (bool) {
    return delegatedTo[operator] == operator;
  }

  /**
   * @notice Returns the OperatorDetails struct associated with an `operator`.
   */
  function operatorDetails(address operator) external view returns (OperatorDetails memory) {
    return _operatorDetails[operator];
  }

  /**
   * @notice Returns the delegationApprover account for an operator
   */
  function delegationApprover(address operator) external view returns (address) {
    return _operatorDetails[operator].delegationApprover;
  }

  /**
   * @notice Returns the stakerOptOutWindow for an operator
   */
  function stakerOptOutWindow(address operator) external view returns (uint256) {
    return _operatorDetails[operator].stakerOptOutWindow;
  }

  /// @notice Given array of pools, returns array of shares for the operator
  function getOperatorShares(address operator, IPool[] memory pools) public view returns (uint256[] memory) {
    uint256[] memory shares = new uint256[](pools.length);
    for (uint256 i = 0; i < pools.length; ++i) {
      shares[i] = operatorShares[operator][pools[i]];
    }
    return shares;
  }

  /**
   * @notice Returns the number of actively-delegatable shares a staker has across all pools.
   * @dev Returns two empty arrays in the case that the Staker has no actively-delegateable shares.
   */
  function getDelegatableShares(address staker) public view returns (IPool[] memory, uint256[] memory) {
    // Get currently active shares and pools for `staker`
    (IPool[] memory poolControllerStrats, uint256[] memory poolControllerShares) = poolController.getDeposits(staker);
    return (poolControllerStrats, poolControllerShares);
  }

  /**
   * @notice Given a list of pools, return the minimum cooldown that must pass to withdraw
   * from all the inputted pools. Return value is >= minWithdrawalDelay as this is the global min withdrawal delay.
   * @param pools The pools to check withdrawal delays for
   */
  function getWithdrawalDelay(IPool[] calldata pools) public view returns (uint256) {
    uint256 withdrawalDelay = minWithdrawalDelay;
    for (uint256 i = 0; i < pools.length; ++i) {
      uint256 currWithdrawalDelay = poolWithdrawalDelay[pools[i]];
      if (currWithdrawalDelay > withdrawalDelay) {
        withdrawalDelay = currWithdrawalDelay;
      }
    }
    return withdrawalDelay;
  }

  /// @notice Returns the keccak256 hash of `withdrawal`.
  function calculateWithdrawalRoot(Withdrawal memory withdrawal) public pure returns (bytes32) {
    return keccak256(abi.encode(withdrawal));
  }

  /**
   * @notice Calculates the digestHash for a `staker` to sign to delegate to an `operator`
   * @param staker The signing staker
   * @param operator The operator who is being delegated to
   * @param expiry The desired expiry time of the staker's signature
   */
  function calculateCurrentStakerDelegationDigestHash(address staker, address operator, uint256 expiry) external view returns (bytes32) {
    // fetch the staker's current nonce
    uint256 currentStakerNonce = stakerNonce[staker];
    // calculate the digest hash
    return calculateStakerDelegationDigestHash(staker, currentStakerNonce, operator, expiry);
  }

  /**
   * @notice Calculates the digest hash to be signed and used in the `delegateToBySignature` function
   * @param staker The signing staker
   * @param _stakerNonce The nonce of the staker. In practice we use the staker's current nonce, stored at `stakerNonce[staker]`
   * @param operator The operator who is being delegated to
   * @param expiry The desired expiry time of the staker's signature
   */
  function calculateStakerDelegationDigestHash(
    address staker,
    uint256 _stakerNonce,
    address operator,
    uint256 expiry
  ) public view returns (bytes32) {
    // calculate the struct hash
    bytes32 stakerStructHash = keccak256(abi.encode(STAKER_DELEGATION_TYPEHASH, staker, operator, _stakerNonce, expiry));
    // calculate the digest hash
    bytes32 stakerDigestHash = keccak256(abi.encodePacked('\x19\x01', domainSeparator(), stakerStructHash));
    return stakerDigestHash;
  }

  /**
   * @notice Calculates the digest hash to be signed by the operator's delegationApprove and used in the `delegateTo` and `delegateToBySignature` functions.
   * @param staker The account delegating their stake
   * @param operator The account receiving delegated stake
   * @param _delegationApprover the operator's `delegationApprover` who will be signing the delegationHash (in general)
   * @param approverSalt A unique and single use value associated with the approver signature.
   * @param expiry Time after which the approver's signature becomes invalid
   */
  function calculateDelegationApprovalDigestHash(
    address staker,
    address operator,
    address _delegationApprover,
    bytes32 approverSalt,
    uint256 expiry
  ) public view returns (bytes32) {
    // calculate the struct hash
    bytes32 approverStructHash = keccak256(
      abi.encode(DELEGATION_APPROVAL_TYPEHASH, _delegationApprover, staker, operator, approverSalt, expiry)
    );
    // calculate the digest hash
    bytes32 approverDigestHash = keccak256(abi.encodePacked('\x19\x01', domainSeparator(), approverStructHash));
    return approverDigestHash;
  }

  /**
   * @dev Recalculates the domain separator when the chainid changes due to a fork.
   */
  function _calculateDomainSeparator() internal view returns (bytes32) {
    return keccak256(abi.encode(DOMAIN_TYPEHASH, keccak256(bytes('BinLayer')), block.chainid, address(this)));
  }
}

contracts/core/DelegationControllerStora.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import '../interfaces/IPoolController.sol';
import '../interfaces/IDelegationController.sol';
import '../interfaces/ISlasher.sol';

/**
 * @title Storage variables for the `DelegationController.sol` contract.
 * @notice This storage contract is separate from the logic to simplify the upgrade process.
 */
abstract contract DelegationControllerStorage is IDelegationController {
  /// @notice The EIP-712 typehash for the contract's domain
  bytes32 public constant DOMAIN_TYPEHASH = keccak256('EIP712Domain(string name,uint256 chainId,address verifyingContract)');

  /// @notice The EIP-712 typehash for the `StakerDelegation` struct used by the contract
  bytes32 public constant STAKER_DELEGATION_TYPEHASH =
    keccak256('StakerDelegation(address staker,address operator,uint256 nonce,uint256 expiry)');

  /// @notice The EIP-712 typehash for the `DelegationApproval` struct used by the contract
  bytes32 public constant DELEGATION_APPROVAL_TYPEHASH =
    keccak256('DelegationApproval(address delegationApprover,address staker,address operator,bytes32 salt,uint256 expiry)');

  /**
   * @notice Original EIP-712 Domain separator for this contract.
   * @dev The domain separator may change in the event of a fork that modifies the ChainID.
   * Use the getter function `domainSeparator` to get the current domain separator for this contract.
   */
  bytes32 internal _DOMAIN_SEPARATOR;

  /// @notice The PoolController.sol contract for BinLayer
  IPoolController public immutable poolController;

  /// @notice The Slasher contract for BinLayer
  ISlasher public immutable slasher;

  // 30 days (60 * 60 * 24 * 30 = 2,592,000)
  uint256 public constant MAX_WITHDRAWAL_DELAY = 2592000;

  /**
   * @notice returns the total number of shares in `pool` that are delegated to `operator`.
   * @notice Mapping: operator => pool => total number of shares in the pool delegated to the operator.
   * @dev By design, the following invariant should hold for each Pool:
   * (operator's shares in delegation manager) = sum (shares above zero of all stakers delegated to operator)
   * = sum (delegateable shares of all stakers delegated to the operator)
   */
  mapping(address => mapping(IPool => uint256)) public operatorShares;

  /**
   * @notice Mapping: operator => OperatorDetails struct
   * @dev This struct is internal with an external getter so we can return an `OperatorDetails memory` object
   */
  mapping(address => OperatorDetails) internal _operatorDetails;

  /**
   * @notice Mapping: staker => operator whom the staker is currently delegated to.
   * @dev Note that returning address(0) indicates that the staker is not actively delegated to any operator.
   */
  mapping(address => address) public delegatedTo;

  /// @notice Mapping: staker => number of signed messages (used in `delegateToBySignature`) from the staker that this contract has already checked.
  mapping(address => uint256) public stakerNonce;

  /**
   * @notice Mapping: delegationApprover => 32-byte salt => whether or not the salt has already been used by the delegationApprover.
   * @dev Salts are used in the `delegateTo` and `delegateToBySignature` functions. Note that these functions only process the delegationApprover's
   * signature + the provided salt if the operator being delegated to has specified a nonzero address as their `delegationApprover`.
   */
  mapping(address => mapping(bytes32 => bool)) public delegationApproverSaltIsSpent;

  /**
   * @notice Global minimum withdrawal delay for all pool withdrawals.
   * In a prior Goerli release, we only had a global min withdrawal delay across all strategies.
   * In addition, we now also configure withdrawal delays on a per-pool basis.
   * To withdraw from a pool, max(minWithdrawalDelay, poolWithdrawalDelay[pool]) number of timestamp must have passed.
   * See mapping poolWithdrawalDelay below for per-pool withdrawal delays.
   */
  uint256 public minWithdrawalDelay;

  /// @notice Mapping: hash of withdrawal inputs, aka 'withdrawalRoot' => whether the withdrawal is pending
  mapping(bytes32 => bool) public pendingWithdrawals;

  /// @notice Mapping: staker => cumulative number of queued withdrawals they have ever initiated.
  /// @dev This only increments (doesn't decrement), and is used to help ensure that otherwise identical withdrawals have unique hashes.
  mapping(address => uint256) public cumulativeWithdrawalsQueued;

  /**
   * @notice Minimum delay enforced by this contract per Pool for completing queued withdrawals. Measured in timestamp, and adjustable by this contract's owner,
   * up to a maximum of `MAX_WITHDRAWAL_DELAY`. Minimum value is 0 (i.e. no delay enforced).
   */
  mapping(IPool => uint256) public poolWithdrawalDelay;

  /// @notice Wrapped token gateway
  address public wrappedTokenGateway;

  constructor(IPoolController _poolController, ISlasher _slasher) {
    poolController = _poolController;
    slasher = _slasher;
  }

  /**
   * @dev This empty reserved space is put in place to allow future versions to add new
   * variables without shifting down storage in the inheritance chain.
   * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
   */
  uint256[49] private __gap;
}

contracts/core/PoolController.sol.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import '@openzeppelin-upgrades/contracts/proxy/utils/Initializable.sol';
import '@openzeppelin-upgrades/contracts/access/OwnableUpgradeable.sol';
import '@openzeppelin-upgrades/contracts/security/ReentrancyGuardUpgradeable.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import '../permissions/Pausable.sol';
import './PoolControllerStorage.sol';
import '../libraries/EIP1271SignatureUtils.sol';
import '../helpers/Errors.sol';

/**
 * @title The primary entry- and exit-point for funds into and out of BinLayer.
 *
 * @notice This contract is for managing deposits in different pools. The main
 * functionalities are:
 * - adding and removing pools that any delegator can deposit into
 * - enabling deposit of assets into specified pool(s)
 */
contract PoolController is Initializable, OwnableUpgradeable, ReentrancyGuardUpgradeable, Pausable, PoolControllerStorage {
  using SafeERC20 for IERC20;

  // index for flag that pauses deposits when set
  uint8 internal constant PAUSED_DEPOSITS = 0;

  // chain id at the time of contract deployment
  uint256 internal immutable ORIGINAL_CHAIN_ID;

  modifier onlyPoolWhitelister() {
    require(msg.sender == poolWhitelister, Errors.NOT_POOL_WHITELISTER);
    _;
  }

  modifier onlyPoolsWhitelistedForDeposit(IPool pool) {
    require(poolIsWhitelistedForDeposit[pool], Errors.POOL_NOT_WHITELISTED);
    _;
  }

  modifier onlyDelegationController() {
    require(msg.sender == address(delegation), Errors.NOT_DELEGATION_CONTROLLER);
    _;
  }

  /**
   * @param _delegation The delegation contract of BinLayer.
   * @param _slasher The primary slashing contract of BinLayer.
   */
  constructor(IDelegationController _delegation, ISlasher _slasher) PoolControllerStorage(_delegation, _slasher) {
    _disableInitializers();
    ORIGINAL_CHAIN_ID = block.chainid;
  }

  // EXTERNAL FUNCTIONS

  /**
   * @notice Initializes the pool controller contract. Sets the `pauserRegistry` (currently **not** modifiable after being set),
   * and transfers contract ownership to the specified `initialOwner`.
   * @param _pauserRegistry Used for access control of pausing.
   * @param initialOwner Ownership of this contract is transferred to this address.
   * @param initialPoolWhitelister The initial value of `poolWhitelister` to set.
   * @param  initialPausedStatus The initial value of `_paused` to set.
   */
  function initialize(
    address initialOwner,
    address initialPoolWhitelister,
    IPauserRegistry _pauserRegistry,
    uint256 initialPausedStatus
  ) external initializer {
    _DOMAIN_SEPARATOR = _calculateDomainSeparator();
    __ReentrancyGuard_init();
    _initializePauser(_pauserRegistry, initialPausedStatus);
    _transferOwnership(initialOwner);
    _setPoolWhitelister(initialPoolWhitelister);
  }

  /**
   * @notice Deposits `amount` of `token` into the specified `pool`, with the resultant shares credited to `msg.sender`
   * @param pool is the specified pool where deposit is to be made,
   * @param token is the denomination in which the deposit is to be made,
   * @param amount is the amount of token to be deposited in the pool by the staker
   * @return shares The amount of new shares in the `pool` created as part of the action.
   * @dev The `msg.sender` must have previously approved this contract to transfer at least `amount` of `token` on their behalf.
   *
   * WARNING: Depositing tokens that allow reentrancy (eg. ERC-777) into a pool is not recommended.  This can lead to attack vectors
   *          where the token balance and corresponding pool shares are not in sync upon reentrancy.
   */
  function depositIntoPool(
    IPool pool,
    IERC20 token,
    uint256 amount
  ) external onlyWhenNotPaused(PAUSED_DEPOSITS) nonReentrant returns (uint256 shares) {
    shares = _depositIntoPool(msg.sender, pool, token, amount);
  }

  /**
   * @notice Deposits `amount` of `token` into the specified `pool`, with the resultant shares credited to `staker`
   * @param staker Staker address
   * @param pool is the specified pool where deposit is to be made,
   * @param token is the denomination in which the deposit is to be made,
   * @param amount is the amount of token to be deposited in the pool by the staker
   * @return shares The amount of new shares in the `pool` created as part of the action.
   * @dev The `msg.sender` must have previously approved this contract to transfer at least `amount` of `token` on their behalf.
   *
   * WARNING: Depositing tokens that allow reentrancy (eg. ERC-777) into a pool is not recommended.  This can lead to attack vectors
   *          where the token balance and corresponding pool shares are not in sync upon reentrancy.
   */
  function depositIntoPoolWithStaker(
    address staker,
    IPool pool,
    IERC20 token,
    uint256 amount
  ) external onlyWhenNotPaused(PAUSED_DEPOSITS) nonReentrant returns (uint256 shares) {
    if (staker != msg.sender) {
      require(!thirdPartyTransfersForbidden[pool], Errors.THIRD_PARTY_TRANSFERS_DISABLED);
    }
    shares = _depositIntoPool(staker, pool, token, amount);
  }

  /**
   * @notice Used for depositing an asset into the specified pool with the resultant shares credited to `staker`,
   * who must sign off on the action.
   * Note that the assets are transferred out/from the `msg.sender`, not from the `staker`; this function is explicitly designed
   * purely to help one address deposit 'for' another.
   * @param pool is the specified pool where deposit is to be made,
   * @param token is the denomination in which the deposit is to be made,
   * @param amount is the amount of token to be deposited in the pool by the staker
   * @param staker the staker that the deposited assets will be credited to
   * @param expiry the timestamp at which the signature expires
   * @param signature is a valid signature from the `staker`. either an ECDSA signature if the `staker` is an EOA, or data to forward
   * following EIP-1271 if the `staker` is a contract
   * @return shares The amount of new shares in the `pool` created as part of the action.
   * @dev The `msg.sender` must have previously approved this contract to transfer at least `amount` of `token` on their behalf.
   * @dev A signature is required for this function to eliminate the possibility of griefing attacks, specifically those
   * targeting stakers who may be attempting to undelegate.
   * @dev Cannot be called if thirdPartyTransfersForbidden is set to true for this pool
   *
   *  WARNING: Depositing tokens that allow reentrancy (eg. ERC-777) into a pool is not recommended.  This can lead to attack vectors
   *          where the token balance and corresponding pool shares are not in sync upon reentrancy
   */
  function depositIntoPoolWithSignature(
    IPool pool,
    IERC20 token,
    uint256 amount,
    address staker,
    uint256 expiry,
    bytes memory signature
  ) external onlyWhenNotPaused(PAUSED_DEPOSITS) nonReentrant returns (uint256 shares) {
    require(!thirdPartyTransfersForbidden[pool], Errors.THIRD_PARTY_TRANSFERS_DISABLED);
    require(expiry >= block.timestamp, Errors.SIGNATURE_EXPIRED);
    // calculate struct hash, then increment `staker`'s nonce
    uint256 nonce = nonces[staker];
    bytes32 structHash = keccak256(abi.encode(DEPOSIT_TYPEHASH, staker, pool, token, amount, nonce, expiry));
    unchecked {
      nonces[staker] = nonce + 1;
    }

    // calculate the digest hash
    bytes32 digestHash = keccak256(abi.encodePacked('\x19\x01', domainSeparator(), structHash));

    /**
     * check validity of signature:
     * 1) if `staker` is an EOA, then `signature` must be a valid ECDSA signature from `staker`,
     * indicating their intention for this action
     * 2) if `staker` is a contract, then `signature` will be checked according to EIP-1271
     */
    EIP1271SignatureUtils.checkSignature_EIP1271(staker, digestHash, signature);

    // deposit the tokens (from the `msg.sender`) and credit the new shares to the `staker`
    shares = _depositIntoPool(staker, pool, token, amount);
  }

  /// @notice Used by the DelegationController.sol to remove a Staker's shares from a particular pool when entering the withdrawal queue
  function removeShares(address staker, IPool pool, uint256 shares) external onlyDelegationController {
    _removeShares(staker, pool, shares);
  }

  /// @notice Used by the DelegationController.sol to award a Staker some shares that have passed through the withdrawal queue
  function addShares(address staker, IERC20 token, IPool pool, uint256 shares) external onlyDelegationController {
    _addShares(staker, token, pool, shares);
  }

  /// @notice Used by the DelegationController.sol to convert withdrawn shares to tokens and send them to a recipient
  function withdrawSharesAsTokens(address recipient, IPool pool, uint256 shares, IERC20 token) external onlyDelegationController {
    pool.withdraw(recipient, token, shares);
  }

  /**
   * If true for a pool, a user cannot depositIntoPoolWithSignature into that pool for another staker
   * and also when performing DelegationController.sol.queueWithdrawals, a staker can only withdraw to themselves.
   * Defaulted to false for all existing pools.
   * @param pool The pool to set `thirdPartyTransfersForbidden` value to
   * @param value bool value to set `thirdPartyTransfersForbidden` to
   */
  function setThirdPartyTransfersForbidden(IPool pool, bool value) external onlyPoolWhitelister {
    _setThirdPartyTransfersForbidden(pool, value);
  }

  /**
   * @notice Owner-only function to change the `poolWhitelister` address.
   * @param newPoolWhitelister new address for the `poolWhitelister`.
   */
  function setPoolWhitelister(address newPoolWhitelister) external onlyOwner {
    _setPoolWhitelister(newPoolWhitelister);
  }

  /**
   * @notice Owner-only function that adds the provided Pools to the 'whitelist' of pools that stakers can deposit into
   * @param poolsToWhitelist Pools that will be added to the `poolIsWhitelistedForDeposit` mapping (if they aren't in it already)
   * @param thirdPartyTransfersForbiddenValues bool values to set `thirdPartyTransfersForbidden` to for each pool
   */
  function addPoolsToDepositWhitelist(
    IPool[] calldata poolsToWhitelist,
    bool[] calldata thirdPartyTransfersForbiddenValues
  ) external onlyPoolWhitelister {
    require(poolsToWhitelist.length == thirdPartyTransfersForbiddenValues.length, Errors.ARRAY_LENGTH_MISMATCH);
    uint256 poolsToWhitelistLength = poolsToWhitelist.length;
    for (uint256 i = 0; i < poolsToWhitelistLength; ) {
      // change storage and emit event only if pool is not already in whitelist
      if (!poolIsWhitelistedForDeposit[poolsToWhitelist[i]]) {
        poolIsWhitelistedForDeposit[poolsToWhitelist[i]] = true;
        emit PoolAddedToDepositWhitelist(poolsToWhitelist[i]);
        _setThirdPartyTransfersForbidden(poolsToWhitelist[i], thirdPartyTransfersForbiddenValues[i]);
      }
      unchecked {
        ++i;
      }
    }
  }

  /**
   * @notice Owner-only function that removes the provided Pools from the 'whitelist' of pools that stakers can deposit into
   * @param poolsToRemoveFromWhitelist Pools that will be removed to the `poolIsWhitelistedForDeposit` mapping (if they are in it)
   */
  function removePoolsFromDepositWhitelist(IPool[] calldata poolsToRemoveFromWhitelist) external onlyPoolWhitelister {
    uint256 poolsToRemoveFromWhitelistLength = poolsToRemoveFromWhitelist.length;
    for (uint256 i = 0; i < poolsToRemoveFromWhitelistLength; ) {
      // change storage and emit event only if pool is already in whitelist
      if (poolIsWhitelistedForDeposit[poolsToRemoveFromWhitelist[i]]) {
        poolIsWhitelistedForDeposit[poolsToRemoveFromWhitelist[i]] = false;
        emit PoolRemovedFromDepositWhitelist(poolsToRemoveFromWhitelist[i]);
        // Set mapping value to default false value
        _setThirdPartyTransfersForbidden(poolsToRemoveFromWhitelist[i], false);
      }
      unchecked {
        ++i;
      }
    }
  }

  // INTERNAL FUNCTIONS

  /**
   * @notice This function adds `shares` for a given `pool` to the `staker` and runs through the necessary update logic.
   * @param staker The address to add shares to
   * @param token The token that is being deposited (used for indexing)
   * @param pool The Pool in which the `staker` is receiving shares
   * @param shares The amount of shares to grant to the `staker`
   * @dev In particular, this function calls `delegation.increaseDelegatedShares(staker, pool, shares)` to ensure that all
   * delegated shares are tracked, increases the stored share amount in `stakerPoolShares[staker][pool]`, and adds `pool`
   * to the `staker`'s list of pools, if it is not in the list already.
   */
  function _addShares(address staker, IERC20 token, IPool pool, uint256 shares) internal {
    // sanity checks on inputs
    require(staker != address(0), Errors.ZERO_ADDRESS_NOT_VALID);
    require(shares != 0, Errors.ZERO_SHARES_NOT_VALID);

    // if they dont have existing shares of this pool, add it to their strats
    if (stakerPoolShares[staker][pool] == 0) {
      require(stakerPoolList[staker].length < MAX_STAKER_POOL_LIST_LENGTH, Errors.DEPOSIT_EXCEEDS_MAX_LENGTH);
      stakerPoolList[staker].push(pool);
    }

    // add the returned shares to their existing shares for this pool
    stakerPoolShares[staker][pool] += shares;

    emit Deposit(staker, token, pool, shares);
  }

  /**
   * @notice Internal function in which `amount` of ERC20 `token` is transferred from `msg.sender` to the Pool-type contract
   * `pool`, with the resulting shares credited to `staker`.
   * @param staker The address that will be credited with the new shares.
   * @param pool The Pool contract to deposit into.
   * @param token The ERC20 token to deposit.
   * @param amount The amount of `token` to deposit.
   * @return shares The amount of *new* shares in `pool` that have been credited to the `staker`.
   */
  function _depositIntoPool(
    address staker,
    IPool pool,
    IERC20 token,
    uint256 amount
  ) internal onlyPoolsWhitelistedForDeposit(pool) returns (uint256 shares) {
    // transfer tokens from the sender to the pool
    token.safeTransferFrom(msg.sender, address(pool), amount);

    // deposit the assets into the specified pool and get the equivalent amount of shares in that pool
    shares = pool.deposit(token, amount);

    // add the returned shares to the staker's existing shares for this pool
    _addShares(staker, token, pool, shares);

    // Increase shares delegated to operator, if needed
    delegation.increaseDelegatedShares(staker, pool, shares);

    return shares;
  }

  /**
   * @notice Decreases the shares that `staker` holds in `pool` by `shareAmount`.
   * @param staker The address to decrement shares from
   * @param pool The pool for which the `staker`'s shares are being decremented
   * @param shareAmount The amount of shares to decrement
   * @dev If the amount of shares represents all of the staker`s shares in said pool,
   * then the pool is removed from stakerPoolList[staker] and 'true' is returned. Otherwise 'false' is returned.
   */
  function _removeShares(address staker, IPool pool, uint256 shareAmount) internal returns (bool) {
    // sanity checks on inputs
    require(shareAmount != 0, Errors.ZERO_SHARES_NOT_VALID);

    //check that the user has sufficient shares
    uint256 userShares = stakerPoolShares[staker][pool];

    require(shareAmount <= userShares, Errors.SHARE_AMOUNT_TOO_HIGH);
    //unchecked arithmetic since we just checked this above
    unchecked {
      userShares = userShares - shareAmount;
    }

    // subtract the shares from the staker's existing shares for this pool
    stakerPoolShares[staker][pool] = userShares;

    // if no existing shares, remove the pool from the staker's dynamic array of pools
    if (userShares == 0) {
      _removePoolFromStakerPoolList(staker, pool);

      // return true in the event that the pool was removed from stakerPoolList[staker]
      return true;
    }
    // return false in the event that the pool was *not* removed from stakerPoolList[staker]
    return false;
  }

  /**
   * @notice Removes `pool` from `staker`'s dynamic array of pools, i.e. from `stakerPoolList[staker]`
   * @param staker The user whose array will have an entry removed
   * @param pool The Pool to remove from `stakerPoolList[staker]`
   */
  function _removePoolFromStakerPoolList(address staker, IPool pool) internal {
    //loop through all of the pools, find the right one, then replace
    uint256 stratsLength = stakerPoolList[staker].length;
    uint256 j = 0;
    for (; j < stratsLength; ) {
      if (stakerPoolList[staker][j] == pool) {
        //replace the pool with the last pool in the list
        stakerPoolList[staker][j] = stakerPoolList[staker][stakerPoolList[staker].length - 1];
        break;
      }
      unchecked {
        ++j;
      }
    }
    // if we didn't find the pool, revert
    require(j != stratsLength, Errors.POOL_NOT_FOUND);
    // pop off the last entry in the list of pools
    stakerPoolList[staker].pop();
  }

  /**
   * @notice Internal function for modifying `thirdPartyTransfersForbidden`.
   * Used inside of the `setThirdPartyTransfersForbidden` and `addPoolsToDepositWhitelist` functions.
   * @param pool The pool to set `thirdPartyTransfersForbidden` value to
   * @param value bool value to set `thirdPartyTransfersForbidden` to
   */
  function _setThirdPartyTransfersForbidden(IPool pool, bool value) internal {
    emit UpdatedThirdPartyTransfersForbidden(pool, value);
    thirdPartyTransfersForbidden[pool] = value;
  }

  /**
   * @notice Internal function for modifying the `poolWhitelister`. Used inside of the `setPoolWhitelister` and `initialize` functions.
   * @param newPoolWhitelister The new address for the `poolWhitelister` to take.
   */
  function _setPoolWhitelister(address newPoolWhitelister) internal {
    emit PoolWhitelisterChanged(poolWhitelister, newPoolWhitelister);
    poolWhitelister = newPoolWhitelister;
  }

  // VIEW FUNCTIONS
  /**
   * @notice Get all details on the staker's deposits and corresponding shares
   * @param staker The staker of interest, whose deposits this function will fetch
   * @return (staker's pools, shares in these pools)
   */
  function getDeposits(address staker) external view returns (IPool[] memory, uint256[] memory) {
    uint256 poolsLength = stakerPoolList[staker].length;
    uint256[] memory shares = new uint256[](poolsLength);

    for (uint256 i = 0; i < poolsLength; ) {
      shares[i] = stakerPoolShares[staker][stakerPoolList[staker][i]];
      unchecked {
        ++i;
      }
    }
    return (stakerPoolList[staker], shares);
  }

  /// @notice Simple getter function that returns `stakerPoolList[staker].length`.
  function stakerPoolListLength(address staker) external view returns (uint256) {
    return stakerPoolList[staker].length;
  }

  /**
   * @notice Getter function for the current EIP-712 domain separator for this contract.
   * @dev The domain separator will change in the event of a fork that changes the ChainID.
   */
  function domainSeparator() public view returns (bytes32) {
    if (block.chainid == ORIGINAL_CHAIN_ID) {
      return _DOMAIN_SEPARATOR;
    } else {
      return _calculateDomainSeparator();
    }
  }

  // @notice Internal function for calculating the current domain separator of this contract
  function _calculateDomainSeparator() internal view returns (bytes32) {
    return keccak256(abi.encode(DOMAIN_TYPEHASH, keccak256(bytes('BinLayer')), block.chainid, address(this)));
  }
}

contracts/core/PoolControllerStorage.sol.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import '../interfaces/IPoolController.sol';
import '../interfaces/IPool.sol';
import '../interfaces/IDelegationController.sol';
import '../interfaces/ISlasher.sol';

/**
 * @title Storage variables for the `PoolController.sol` contract.
 * @notice This storage contract is separate from the logic to simplify the upgrade process.
 */
abstract contract PoolControllerStorage is IPoolController {
  /// @notice The EIP-712 typehash for the contract's domain
  bytes32 public constant DOMAIN_TYPEHASH = keccak256('EIP712Domain(string name,uint256 chainId,address verifyingContract)');
  /// @notice The EIP-712 typehash for the deposit struct used by the contract
  bytes32 public constant DEPOSIT_TYPEHASH =
    keccak256('Deposit(address staker,address pool,address token,uint256 amount,uint256 nonce,uint256 expiry)');
  // maximum length of dynamic arrays in `stakerPoolList` mapping, for sanity's sake
  uint8 internal constant MAX_STAKER_POOL_LIST_LENGTH = 32;

  // system contracts
  IDelegationController public immutable delegation;
  ISlasher public immutable slasher;

  /**
   * @notice Original EIP-712 Domain separator for this contract.
   * @dev The domain separator may change in the event of a fork that modifies the ChainID.
   * Use the getter function `domainSeparator` to get the current domain separator for this contract.
   */
  bytes32 internal _DOMAIN_SEPARATOR;
  // staker => number of signed deposit nonce (used in depositIntoPoolWithSignature)
  mapping(address => uint256) public nonces;
  /// @notice Permissioned role, which can be changed by the contract owner. Has the ability to edit the pool whitelist
  address public poolWhitelister;
  /// @notice Mapping: staker => Pool => number of shares which they currently hold
  mapping(address => mapping(IPool => uint256)) public stakerPoolShares;
  /// @notice Mapping: staker => array of strategies in which they have nonzero shares
  mapping(address => IPool[]) public stakerPoolList;
  /// @notice Mapping: pool => whether or not stakers are allowed to deposit into it
  mapping(IPool => bool) public poolIsWhitelistedForDeposit;

  /**
   * @notice Mapping: pool => whether or not stakers are allowed to transfer pool shares to another address
   * if true for a pool, a user cannot depositIntoPoolWithSignature into that pool for another staker
   * and also when performing queueWithdrawals, a staker can only withdraw to themselves
   */
  mapping(IPool => bool) public thirdPartyTransfersForbidden;

  constructor(IDelegationController _delegation, ISlasher _slasher) {
    delegation = _delegation;
    slasher = _slasher;
  }

  /**
   * @dev This empty reserved space is put in place to allow future versions to add new
   * variables without shifting down storage in the inheritance chain.
   * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
   */
  uint256[49] private __gap;
}

contracts/core/RewardsCoordinator.sol.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import '@openzeppelin-upgrades/contracts/proxy/utils/Initializable.sol';
import '@openzeppelin-upgrades/contracts/access/OwnableUpgradeable.sol';
import '@openzeppelin-upgrades/contracts/security/ReentrancyGuardUpgradeable.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import '../libraries/Merkle.sol';
import '../libraries/EIP1271SignatureUtils.sol';
import '../permissions/Pausable.sol';
import './RewardsCoordinatorStorage.sol';
import '../helpers/Errors.sol';

/**
 * @title RewardsCoordinator
 * @notice  This is the contract for rewards in BinLayer. The main functionalities of this contract are
 * - enabling any ERC20 rewards from AVSs to their operators and stakers for a given time range
 * - allowing stakers and operators to claim their earnings including a commission bips for operators
 * - allowing the protocol to provide ERC20 tokens to stakers over a specified time range
 */
contract RewardsCoordinator is Initializable, OwnableUpgradeable, Pausable, ReentrancyGuardUpgradeable, RewardsCoordinatorStorage {
  using SafeERC20 for IERC20;

  /// @notice The EIP-712 typehash for the contract's domain
  bytes32 internal constant DOMAIN_TYPEHASH = keccak256('EIP712Domain(string name,uint256 chainId,address verifyingContract)');
  /// @dev Chain ID at the time of contract deployment
  uint256 internal immutable ORIGINAL_CHAIN_ID;
  /// @notice The maximum rewards token amount for a single rewards submission, constrained by off-chain calculation
  uint256 internal constant MAX_REWARDS_AMOUNT = 1e38 - 1;

  /// @dev Index for flag that pauses calling createAVSRewardsSubmission
  uint8 internal constant PAUSED_AVS_REWARDS_SUBMISSION = 0;
  /// @dev Index for flag that pauses calling createRewardsForAllSubmission
  uint8 internal constant PAUSED_REWARDS_FOR_ALL_SUBMISSION = 1;
  /// @dev Index for flag that pauses calling processClaim
  uint8 internal constant PAUSED_PROCESS_CLAIM = 2;
  /// @dev Index for flag that pauses submitRoots and disableRoot
  uint8 internal constant PAUSED_SUBMIT_DISABLE_ROOTS = 3;

  /// @dev Salt for the earner leaf, meant to distinguish from tokenLeaf since they have the same sized data
  uint8 internal constant EARNER_LEAF_SALT = 0;
  /// @dev Salt for the token leaf, meant to distinguish from earnerLeaf since they have the same sized data
  uint8 internal constant TOKEN_LEAF_SALT = 1;

  modifier onlyRewardsUpdater() {
    require(msg.sender == rewardsUpdater, Errors.NOT_REWARDS_UPDATER);
    _;
  }

  modifier onlyRewardsForAllSubmitter() {
    require(isRewardsForAllSubmitter[msg.sender], Errors.NOT_CREATE_REWARDS_FOR_ALL_SUBMITTER);
    _;
  }

  /// @dev Sets the immutable variables for the contract
  constructor(
    IDelegationController _delegationController,
    IPoolController _poolController,
    uint32 _CALCULATION_INTERVAL_SECONDS,
    uint32 _MAX_REWARDS_DURATION,
    uint32 _MAX_RETROACTIVE_LENGTH,
    uint32 _MAX_FUTURE_LENGTH,
    uint32 __GENESIS_REWARDS_TIMESTAMP
  )
    RewardsCoordinatorStorage(
      _delegationController,
      _poolController,
      _CALCULATION_INTERVAL_SECONDS,
      _MAX_REWARDS_DURATION,
      _MAX_RETROACTIVE_LENGTH,
      _MAX_FUTURE_LENGTH,
      __GENESIS_REWARDS_TIMESTAMP
    )
  {
    _disableInitializers();
    ORIGINAL_CHAIN_ID = block.chainid;
  }

  /**
   * @dev Initializes the addresses of the initial owner, pauser registry, rewardsUpdater and
   * configures the initial paused status, activationDelay, and globalOperatorCommissionBips.
   */
  function initialize(
    address initialOwner,
    IPauserRegistry _pauserRegistry,
    uint256 initialPausedStatus,
    address _rewardsUpdater,
    uint32 _activationDelay,
    uint16 _globalCommissionBips
  ) external initializer {
    _DOMAIN_SEPARATOR = _calculateDomainSeparator();
    _initializePauser(_pauserRegistry, initialPausedStatus);
    _transferOwnership(initialOwner);
    _setRewardsUpdater(_rewardsUpdater);
    _setActivationDelay(_activationDelay);
    _setGlobalOperatorCommission(_globalCommissionBips);
  }

  /*******************************************************************************
                            EXTERNAL FUNCTIONS 
    *******************************************************************************/

  /**
   * @notice Creates a new rewards submission on behalf of an AVS, to be split amongst the
   * set of stakers delegated to operators who are registered to the `avs`
   * @param rewardsSubmissions The rewards submissions being created
   * @dev Expected to be called by the ServiceManager of the AVS on behalf of which the submission is being made
   * @dev The duration of the `rewardsSubmission` cannot exceed `MAX_REWARDS_DURATION`
   * @dev The tokens are sent to the `RewardsCoordinator` contract
   * @dev pools must be in ascending order of addresses to check for duplicates
   * @dev This function will revert if the `rewardsSubmission` is malformed,
   * e.g. if the `pools` and `weights` arrays are of non-equal lengths
   */
  function createAVSRewardsSubmission(
    RewardsSubmission[] calldata rewardsSubmissions
  ) external onlyWhenNotPaused(PAUSED_AVS_REWARDS_SUBMISSION) nonReentrant {
    for (uint256 i = 0; i < rewardsSubmissions.length; i++) {
      RewardsSubmission calldata rewardsSubmission = rewardsSubmissions[i];
      uint256 nonce = submissionNonce[msg.sender];
      bytes32 rewardsSubmissionHash = keccak256(abi.encode(msg.sender, nonce, rewardsSubmission));

      _validateRewardsSubmission(rewardsSubmission);

      isAVSRewardsSubmissionHash[msg.sender][rewardsSubmissionHash] = true;
      submissionNonce[msg.sender] = nonce + 1;

      emit AVSRewardsSubmissionCreated(msg.sender, nonce, rewardsSubmissionHash, rewardsSubmission);
      rewardsSubmission.token.safeTransferFrom(msg.sender, address(this), rewardsSubmission.amount);
    }
  }

  /**
   * @notice similar to `createAVSRewardsSubmission` except the rewards are split amongst *all* stakers
   * rather than just those delegated to operators who are registered to a single avs and is
   * a permissioned call based on isRewardsForAllSubmitter mapping.
   * @param rewardsSubmissions The rewards submissions being created
   */
  function createRewardsForAllSubmission(
    RewardsSubmission[] calldata rewardsSubmissions
  ) external onlyWhenNotPaused(PAUSED_REWARDS_FOR_ALL_SUBMISSION) onlyRewardsForAllSubmitter nonReentrant {
    for (uint256 i = 0; i < rewardsSubmissions.length; i++) {
      RewardsSubmission calldata rewardsSubmission = rewardsSubmissions[i];
      uint256 nonce = submissionNonce[msg.sender];
      bytes32 rewardsSubmissionForAllHash = keccak256(abi.encode(msg.sender, nonce, rewardsSubmission));

      _validateRewardsSubmission(rewardsSubmission);

      isRewardsSubmissionForAllHash[msg.sender][rewardsSubmissionForAllHash] = true;
      submissionNonce[msg.sender] = nonce + 1;

      emit RewardsSubmissionForAllCreated(msg.sender, nonce, rewardsSubmissionForAllHash, rewardsSubmission);
      rewardsSubmission.token.safeTransferFrom(msg.sender, address(this), rewardsSubmission.amount);
    }
  }

  /**
   * @notice Claim rewards against a given root (read from _distributionRoots[claim.rootIndex]).
   * Earnings are cumulative so earners don't have to claim against all distribution roots they have earnings for,
   * they can simply claim against the latest root and the contract will calculate the difference between
   * their cumulativeEarnings and cumulativeClaimed. This difference is then transferred to recipient address.
   * @param claim The RewardsMerkleClaim to be processed.
   * Contains the root index, earner, token leaves, and required proofs
   * @param recipient The address recipient that receives the ERC20 rewards
   * @dev only callable by the valid claimer, that is
   * if claimerFor[claim.earner] is address(0) then only the earner can claim, otherwise only
   * claimerFor[claim.earner] can claim the rewards.
   */
  function processClaim(
    RewardsMerkleClaim calldata claim,
    address recipient
  ) external onlyWhenNotPaused(PAUSED_PROCESS_CLAIM) nonReentrant {
    DistributionRoot memory root = _distributionRoots[claim.rootIndex];
    _checkClaim(claim, root);
    // If claimerFor earner is not set, claimer is by default the earner. Else set to claimerFor
    address earner = claim.earnerLeaf.earner;
    address claimer = claimerFor[earner];
    if (claimer == address(0)) {
      claimer = earner;
    }
    require(msg.sender == claimer, Errors.NOT_CLAIMER);
    for (uint256 i = 0; i < claim.tokenIndices.length; i++) {
      TokenTreeMerkleLeaf calldata tokenLeaf = claim.tokenLeaves[i];

      uint256 currCumulativeClaimed = cumulativeClaimed[earner][tokenLeaf.token];
      require(tokenLeaf.cumulativeEarnings > currCumulativeClaimed, Errors.CUMULATIVE_EARNINGS_MUST_BE_GREATER_THAN_CUMULATIVE_LAIMED);

      // Calculate amount to claim and update cumulativeClaimed
      uint256 claimAmount = tokenLeaf.cumulativeEarnings - currCumulativeClaimed;
      cumulativeClaimed[earner][tokenLeaf.token] = tokenLeaf.cumulativeEarnings;

      tokenLeaf.token.safeTransfer(recipient, claimAmount);
      emit RewardsClaimed(root.root, earner, claimer, recipient, tokenLeaf.token, claimAmount);
    }
  }

  /**
   * @notice Creates a new distribution root. activatedAt is set to block.timestamp + activationDelay
   * @param root The merkle root of the distribution
   * @param rewardsCalculationEndTimestamp The timestamp until which rewards have been calculated
   * @dev Only callable by the rewardsUpdater
   */
  function submitRoot(
    bytes32 root,
    uint32 rewardsCalculationEndTimestamp
  ) external onlyWhenNotPaused(PAUSED_SUBMIT_DISABLE_ROOTS) onlyRewardsUpdater {
    require(rewardsCalculationEndTimestamp > currRewardsCalculationEndTimestamp, Errors.NEW_ROOT_MUST_BE_NEWER_CALCULATED_PERIOD);
    require(rewardsCalculationEndTimestamp < block.timestamp, Errors.CANNOT_BE_IN_THE_FEATURE);
    uint32 rootIndex = uint32(_distributionRoots.length);
    uint32 activatedAt = uint32(block.timestamp) + activationDelay;
    _distributionRoots.push(
      DistributionRoot({
        root: root,
        activatedAt: activatedAt,
        rewardsCalculationEndTimestamp: rewardsCalculationEndTimestamp,
        disabled: false
      })
    );
    currRewardsCalculationEndTimestamp = rewardsCalculationEndTimestamp;
    emit DistributionRootSubmitted(rootIndex, root, rewardsCalculationEndTimestamp, activatedAt);
  }

  /**
   * @notice allow the rewardsUpdater to disable/cancel a pending root submission in case of an error
   * @param rootIndex The index of the root to be disabled
   */
  function disableRoot(uint32 rootIndex) external onlyWhenNotPaused(PAUSED_SUBMIT_DISABLE_ROOTS) onlyRewardsUpdater {
    require(rootIndex < _distributionRoots.length, Errors.INVALID_ROOT_INDEX);
    DistributionRoot storage root = _distributionRoots[rootIndex];
    require(!root.disabled, Errors.ROOT_ALREADY_DISABLED);
    require(block.timestamp < root.activatedAt, Errors.ROOT_ALREADY_ACTIVATED);
    root.disabled = true;
    emit DistributionRootDisabled(rootIndex);
  }

  /**
   * @notice Sets the address of the entity that can call `processClaim` on behalf of the earner (msg.sender)
   * @param claimer The address of the entity that can call `processClaim` on behalf of the earner
   * @dev Only callable by the `earner`
   */
  function setClaimerFor(address claimer) external {
    address earner = msg.sender;
    address prevClaimer = claimerFor[earner];
    claimerFor[earner] = claimer;
    emit ClaimerForSet(earner, prevClaimer, claimer);
  }

  /**
   * @notice Sets the delay in timestamp before a posted root can be claimed against
   * @dev Only callable by the contract owner
   * @param _activationDelay The new value for activationDelay
   */
  function setActivationDelay(uint32 _activationDelay) external onlyOwner {
    _setActivationDelay(_activationDelay);
  }

  /**
   * @notice Sets the global commission for all operators across all avss
   * @dev Only callable by the contract owner
   * @param _globalCommissionBips The commission for all operators across all avss
   */
  function setGlobalOperatorCommission(uint16 _globalCommissionBips) external onlyOwner {
    _setGlobalOperatorCommission(_globalCommissionBips);
  }

  /**
   * @notice Sets the permissioned `rewardsUpdater` address which can post new roots
   * @dev Only callable by the contract owner
   * @param _rewardsUpdater The address of the new rewardsUpdater
   */
  function setRewardsUpdater(address _rewardsUpdater) external onlyOwner {
    _setRewardsUpdater(_rewardsUpdater);
  }

  /**
   * @notice Sets the permissioned `rewardsForAllSubmitter` address which can submit createRewardsForAllSubmission
   * @dev Only callable by the contract owner
   * @param _submitter The address of the rewardsForAllSubmitter
   * @param _newValue The new value for isRewardsForAllSubmitter
   */
  function setRewardsForAllSubmitter(address _submitter, bool _newValue) external onlyOwner {
    bool prevValue = isRewardsForAllSubmitter[_submitter];
    emit RewardsForAllSubmitterSet(_submitter, prevValue, _newValue);
    isRewardsForAllSubmitter[_submitter] = _newValue;
  }

  /*******************************************************************************
                            INTERNAL FUNCTIONS
    *******************************************************************************/

  /**
   * @notice Validate a RewardsSubmission. Called from both `createAVSRewardsSubmission` and `createRewardsForAllSubmission`
   */
  function _validateRewardsSubmission(RewardsSubmission calldata rewardsSubmission) internal view {
    require(rewardsSubmission.poolsAndMultipliers.length > 0, Errors.NO_POOLS_SET);
    require(rewardsSubmission.amount > 0, Errors.AMOUNT_CANNOT_BE_ZERO);
    require(rewardsSubmission.amount <= MAX_REWARDS_AMOUNT, Errors.AMOUNT_TOO_LARGE);
    require(rewardsSubmission.duration <= MAX_REWARDS_DURATION, Errors.DURATION_EXCEEDS_MAX_REWARDS_DURATION);
    require(
      rewardsSubmission.duration % CALCULATION_INTERVAL_SECONDS == 0,
      Errors.DURATION_MUST_BE_MULTIPLE_OF_CALCULATION_INTERVAL_SECONDS
    );
    require(
      rewardsSubmission.startTimestamp % CALCULATION_INTERVAL_SECONDS == 0,
      Errors.STARTTIMESTAMP_MUST_BE_MULTIPLE_OF_CALCULATION_INTERVAL_SECONDS
    );
    require(
      block.timestamp - MAX_RETROACTIVE_LENGTH <= rewardsSubmission.startTimestamp &&
        GENESIS_REWARDS_TIMESTAMP <= rewardsSubmission.startTimestamp,
      Errors.STARTTIMESTAMP_TOO_FAR_IN_THE_PAST
    );
    require(rewardsSubmission.startTimestamp <= block.timestamp + MAX_FUTURE_LENGTH, Errors.STARTTIMESTAMP_TOO_FAR_IN_THE_FUTURE);

    // Require rewardsSubmission is for whitelisted pool
    address currAddress = address(0);
    for (uint256 i = 0; i < rewardsSubmission.poolsAndMultipliers.length; ++i) {
      IPool pool = rewardsSubmission.poolsAndMultipliers[i].pool;
      require(poolController.poolIsWhitelistedForDeposit(pool), Errors.INVALID_POOL_CONSIDERED);
      require(currAddress < address(pool), Errors.POOLS_MUST_BE_ASCENDING);
      currAddress = address(pool);
    }
  }

  function _checkClaim(RewardsMerkleClaim calldata claim, DistributionRoot memory root) internal view {
    require(!root.disabled, Errors.ROOT_IS_DISABLED);
    require(block.timestamp >= root.activatedAt, Errors.ROOT_NOT_ACTIVATED_YET);
    require(claim.tokenIndices.length == claim.tokenTreeProofs.length, Errors.ARRAY_LENGTH_MISMATCH);
    require(claim.tokenTreeProofs.length == claim.tokenLeaves.length, Errors.ARRAY_LENGTH_MISMATCH);

    // Verify inclusion of earners leaf (earner, earnerTokenRoot) in the distribution root
    _verifyEarnerClaimProof({
      root: root.root,
      earnerLeafIndex: claim.earnerIndex,
      earnerProof: claim.earnerTreeProof,
      earnerLeaf: claim.earnerLeaf
    });
    // For each of the tokenLeaf proofs, verify inclusion of token tree leaf again the earnerTokenRoot
    for (uint256 i = 0; i < claim.tokenIndices.length; ++i) {
      _verifyTokenClaimProof({
        earnerTokenRoot: claim.earnerLeaf.earnerTokenRoot,
        tokenLeafIndex: claim.tokenIndices[i],
        tokenProof: claim.tokenTreeProofs[i],
        tokenLeaf: claim.tokenLeaves[i]
      });
    }
  }

  /**
   * @notice verify inclusion of the token claim proof in the earner token root hash (earnerTokenRoot).
   * The token leaf comprises of the IERC20 token and cumulativeAmount of earnings.
   * @param earnerTokenRoot root hash of the earner token subtree
   * @param tokenLeafIndex index of the token leaf
   * @param tokenProof proof of the token leaf in the earner token subtree
   * @param tokenLeaf token leaf to be verified
   */
  function _verifyTokenClaimProof(
    bytes32 earnerTokenRoot,
    uint32 tokenLeafIndex,
    bytes calldata tokenProof,
    TokenTreeMerkleLeaf calldata tokenLeaf
  ) internal pure {
    // Validate index size so that there aren't multiple valid indices for the given proof
    // index can't be greater than 2**(tokenProof/32)
    require(tokenLeafIndex < (1 << (tokenProof.length / 32)), Errors.INVALID_TOKEN_LEAF_INDEX);

    // Verify inclusion of token leaf
    bytes32 tokenLeafHash = calculateTokenLeafHash(tokenLeaf);
    require(
      Merkle.verifyInclusionKeccak({root: earnerTokenRoot, index: tokenLeafIndex, proof: tokenProof, leaf: tokenLeafHash}),
      Errors.INVALID_TOKEN_CLAIM_PROOF
    );
  }

  /**
   * @notice verify inclusion of earner claim proof in the distribution root. This verifies
   * the inclusion of the earner and earnerTokenRoot hash in the tree. The token claims are proven separately
   * against the earnerTokenRoot hash (see _verifyTokenClaimProof). The earner leaf comprises of (earner, earnerTokenRoot)
   * @param root distribution root that should be read from storage
   * @param earnerLeafIndex index of the earner leaf
   * @param earnerProof proof of the earners account root in the merkle tree
   * @param earnerLeaf leaf of earner merkle tree containing the earner address and earner's token root hash
   */
  function _verifyEarnerClaimProof(
    bytes32 root,
    uint32 earnerLeafIndex,
    bytes calldata earnerProof,
    EarnerTreeMerkleLeaf calldata earnerLeaf
  ) internal pure {
    // Validate index size so that there aren't multiple valid indices for the given proof
    // index can't be greater than 2**(earnerProof/32)
    require(earnerLeafIndex < (1 << (earnerProof.length / 32)), Errors.INVALID_EARNER_LEAF_INDEX);
    // Verify inclusion of earner leaf
    bytes32 earnerLeafHash = calculateEarnerLeafHash(earnerLeaf);
    // forgefmt: disable-next-item
    require(
      Merkle.verifyInclusionKeccak({root: root, index: earnerLeafIndex, proof: earnerProof, leaf: earnerLeafHash}),
      Errors.INVALID_EARNER_CLAIM_PROOF
    );
  }

  function _setActivationDelay(uint32 _activationDelay) internal {
    emit ActivationDelaySet(activationDelay, _activationDelay);
    activationDelay = _activationDelay;
  }

  function _setGlobalOperatorCommission(uint16 _globalCommissionBips) internal {
    emit GlobalCommissionBipsSet(globalOperatorCommissionBips, _globalCommissionBips);
    globalOperatorCommissionBips = _globalCommissionBips;
  }

  function _setRewardsUpdater(address _rewardsUpdater) internal {
    emit RewardsUpdaterSet(rewardsUpdater, _rewardsUpdater);
    rewardsUpdater = _rewardsUpdater;
  }

  /*******************************************************************************
                            VIEW FUNCTIONS
    *******************************************************************************/

  /// @notice return the hash of the earner's leaf
  function calculateEarnerLeafHash(EarnerTreeMerkleLeaf calldata leaf) public pure returns (bytes32) {
    return keccak256(abi.encodePacked(EARNER_LEAF_SALT, leaf.earner, leaf.earnerTokenRoot));
  }

  /// @notice returns the hash of the earner's token leaf
  function calculateTokenLeafHash(TokenTreeMerkleLeaf calldata leaf) public pure returns (bytes32) {
    return keccak256(abi.encodePacked(TOKEN_LEAF_SALT, leaf.token, leaf.cumulativeEarnings));
  }

  /// @notice returns 'true' if the claim would currently pass the check in `processClaims`
  /// but will revert if not valid
  function checkClaim(RewardsMerkleClaim calldata claim) public view returns (bool) {
    _checkClaim(claim, _distributionRoots[claim.rootIndex]);
    return true;
  }

  /// @notice the commission for a specific operator for a specific avs
  /// NOTE: Currently unused and simply returns the globalOperatorCommissionBips value but will be used in future release
  function operatorCommissionBips(address operator, address avs) external view returns (uint16) {
    return globalOperatorCommissionBips;
  }

  function getDistributionRootsLength() public view returns (uint256) {
    return _distributionRoots.length;
  }

  function getDistributionRootAtIndex(uint256 index) external view returns (DistributionRoot memory) {
    return _distributionRoots[index];
  }

  /// @notice loop through the distribution roots from reverse and get latest root that is not disabled
  function getCurrentDistributionRoot() external view returns (DistributionRoot memory) {
    return _distributionRoots[_distributionRoots.length - 1];
  }

  /// @notice loop through the distribution roots from reverse and get latest root that is not disabled and activated
  /// i.e. a root that can be claimed against
  function getCurrentClaimableDistributionRoot() external view returns (DistributionRoot memory) {
    for (uint256 i = _distributionRoots.length; i > 0; i--) {
      DistributionRoot memory root = _distributionRoots[i - 1];
      if (!root.disabled && block.timestamp >= root.activatedAt) {
        return root;
      }
    }
  }

  /// @notice loop through distribution roots from reverse and return hash
  function getRootIndexFromHash(bytes32 rootHash) public view returns (uint32) {
    for (uint32 i = uint32(_distributionRoots.length); i > 0; i--) {
      if (_distributionRoots[i - 1].root == rootHash) {
        return i - 1;
      }
    }
    revert(Errors.ROOT_NOT_FOUND);
  }

  /**
   * @notice Getter function for the current EIP-712 domain separator for this contract.
   *
   * @dev The domain separator will change in the event of a fork that changes the ChainID.
   * @dev By introducing a domain separator the DApp developers are guaranteed that there can be no signature collision.
   * for more detailed information please read EIP-712.
   */
  function domainSeparator() public view returns (bytes32) {
    if (block.chainid == ORIGINAL_CHAIN_ID) {
      return _DOMAIN_SEPARATOR;
    } else {
      return _calculateDomainSeparator();
    }
  }

  /**
   * @dev Recalculates the domain separator when the chainid changes due to a fork.
   */
  function _calculateDomainSeparator() internal view returns (bytes32) {
    return keccak256(abi.encode(DOMAIN_TYPEHASH, keccak256(bytes('BinLayer')), block.chainid, address(this)));
  }
}

contracts/core/RewardsCoordinatorStorage.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import '../interfaces/IAVSDirectory.sol';
import '../interfaces/IPoolController.sol';
import '../interfaces/IDelegationController.sol';
import '../interfaces/IRewardsCoordinator.sol';

/**
 * @title Storage variables for the `RewardsCoordinator` contract.
 */
abstract contract RewardsCoordinatorStorage is IRewardsCoordinator {
  /**
   *
   *                            CONSTANTS AND IMMUTABLES
   *
   */

  /// @notice The interval in seconds at which the calculation for rewards distribution is done.
  /// @dev RewardsSubmission durations must be multiples of this interval. This is going to be configured to 1 week
  uint32 public immutable CALCULATION_INTERVAL_SECONDS;
  /// @notice The maximum amount of time (seconds) that a rewards submission can span over
  uint32 public immutable MAX_REWARDS_DURATION;
  /// @notice max amount of time (seconds) that a rewards submission can start in the past
  uint32 public immutable MAX_RETROACTIVE_LENGTH;
  /// @notice max amount of time (seconds) that a rewards submission can start in the future
  uint32 public immutable MAX_FUTURE_LENGTH;
  /// @notice absolute min timestamp (seconds) that a rewards submission can start at
  uint32 public immutable GENESIS_REWARDS_TIMESTAMP;
  /// @notice The cadence at which a snapshot is taken offchain for calculating rewards distributions
  uint32 internal constant SNAPSHOT_CADENCE = 1 days;

  /// @notice The DelegationController contract for BinLayer
  IDelegationController public immutable delegationController;

  /// @notice The PoolController contract for BinLayer
  IPoolController public immutable poolController;

  /**
   *
   *                                    STORAGE
   *
   */

  /**
   * @notice Original EIP-712 Domain separator for this contract.
   * @dev The domain separator may change in the event of a fork that modifies the ChainID.
   * Use the getter function `domainSeparator` to get the current domain separator for this contract.
   */
  bytes32 internal _DOMAIN_SEPARATOR;

  /**
   * @notice List of roots submited by the rewardsUpdater
   * @dev Array is internal with an external getter so we can return a `DistributionRoot[] memory` object
   */
  DistributionRoot[] internal _distributionRoots;

  /// Slot 3
  /// @notice The address of the entity that can update the contract with new merkle roots
  address public rewardsUpdater;
  /// @notice Delay in timestamp (seconds) before a posted root can be claimed against
  uint32 public activationDelay;
  /// @notice Timestamp for last submitted DistributionRoot
  uint32 public currRewardsCalculationEndTimestamp;

  /// Slot 4
  /// @notice the commission for all operators across all avss
  uint16 public globalOperatorCommissionBips;

  /// @notice Mapping: earner => the address of the entity who can call `processClaim` on behalf of the earner
  mapping(address => address) public claimerFor;

  /// @notice Mapping: earner => token => total amount claimed
  mapping(address => mapping(IERC20 => uint256)) public cumulativeClaimed;

  /// @notice Used for unique rewardsSubmissionHashes per AVS and for RewardsForAllSubmitters
  mapping(address => uint256) public submissionNonce;
  /// @notice Mapping: avs => avsRewardsSubmissionHash => bool to check if rewards submission hash has been submitted
  mapping(address => mapping(bytes32 => bool)) public isAVSRewardsSubmissionHash;
  /// @notice Mapping: avs => rewardsSubmissionForALlHash => bool to check if rewards submission hash for all has been submitted
  mapping(address => mapping(bytes32 => bool)) public isRewardsSubmissionForAllHash;
  /// @notice Mapping: address => bool to check if the address is permissioned to call createRewardsForAllSubmission
  mapping(address => bool) public isRewardsForAllSubmitter;

  constructor(
    IDelegationController _delegationController,
    IPoolController _poolController,
    uint32 _CALCULATION_INTERVAL_SECONDS,
    uint32 _MAX_REWARDS_DURATION,
    uint32 _MAX_RETROACTIVE_LENGTH,
    uint32 _MAX_FUTURE_LENGTH,
    uint32 _GENESIS_REWARDS_TIMESTAMP
  ) {
    require(
      _GENESIS_REWARDS_TIMESTAMP % _CALCULATION_INTERVAL_SECONDS == 0,
      'RewardsCoordinator: GENESIS_REWARDS_TIMESTAMP must be a multiple of CALCULATION_INTERVAL_SECONDS'
    );
    require(
      _CALCULATION_INTERVAL_SECONDS % SNAPSHOT_CADENCE == 0,
      'RewardsCoordinator: CALCULATION_INTERVAL_SECONDS must be a multiple of SNAPSHOT_CADENCE'
    );
    delegationController = _delegationController;
    poolController = _poolController;
    CALCULATION_INTERVAL_SECONDS = _CALCULATION_INTERVAL_SECONDS;
    MAX_REWARDS_DURATION = _MAX_REWARDS_DURATION;
    MAX_RETROACTIVE_LENGTH = _MAX_RETROACTIVE_LENGTH;
    MAX_FUTURE_LENGTH = _MAX_FUTURE_LENGTH;
    GENESIS_REWARDS_TIMESTAMP = _GENESIS_REWARDS_TIMESTAMP;
  }

  /**
   * @dev This empty reserved space is put in place to allow future versions to add new
   * variables without shifting down storage in the inheritance chain.
   * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
   */
  uint256[49] private __gap;
}

contracts/core/Slasher.sol.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import '../interfaces/ISlasher.sol';
import '../interfaces/IDelegationController.sol';
import '../interfaces/IPoolController.sol';
import '../libraries/StructuredLinkedList.sol';
import '../permissions/Pausable.sol';
import '@openzeppelin-upgrades/contracts/access/OwnableUpgradeable.sol';
import '@openzeppelin-upgrades/contracts/proxy/utils/Initializable.sol';

/**
 * @notice This contract is not in use as of the BinLayer release.
 */
contract Slasher is Initializable, OwnableUpgradeable, ISlasher, Pausable {
  constructor(IPoolController, IDelegationController) {}

  function initialize(address, IPauserRegistry, uint256) external {}

  function optIntoSlashing(address) external {}

  function freezeOperator(address) external {}

  function resetFrozenStatus(address[] calldata) external {}

  function recordFirstStakeUpdate(address, uint32) external {}

  function recordStakeUpdate(address, uint32, uint32, uint256) external {}

  function recordLastStakeUpdateAndRevokeSlashingAbility(address, uint32) external {}

  function poolController() external view returns (IPoolController) {}

  function delegation() external view returns (IDelegationController) {}

  function isFrozen(address) external view returns (bool) {}

  function canSlash(address, address) external view returns (bool) {}

  function contractCanSlashOperatorUntilTimestamp(address, address) external view returns (uint32) {}

  function latestUpdateTimestamp(address, address) external view returns (uint32) {}

  function getCorrectValueForInsertAfter(address, uint32) external view returns (uint256) {}

  function canWithdraw(address, uint32, uint256) external returns (bool) {}

  function operatorToMiddlewareTimes(address, uint256) external view returns (MiddlewareTimes memory) {}

  function middlewareTimesLength(address) external view returns (uint256) {}

  function getMiddlewareTimesIndexStalestUpdateTimestamp(address, uint32) external view returns (uint32) {}

  function getMiddlewareTimesIndexServeUntilTimestamp(address, uint32) external view returns (uint32) {}

  function operatorWhitelistedContractsLinkedListSize(address) external view returns (uint256) {}

  function operatorWhitelistedContractsLinkedListEntry(address, address) external view returns (bool, uint256, uint256) {}

  function whitelistedContractDetails(address, address) external view returns (MiddlewareDetails memory) {}
}

contracts/interfaces/IAVSDirectory.sol.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import './ISignatureUtils.sol';

interface IAVSDirectory is ISignatureUtils {
  /// @notice Enum representing the status of an operator's registration with an AVS
  enum OperatorAVSRegistrationStatus {
    UNREGISTERED, // Operator not registered to AVS
    REGISTERED // Operator registered to AVS
  }

  /**
   * @notice Emitted when @param avs indicates that they are updating their MetadataURI string
   * @dev Note that these strings are *never stored in storage* and are instead purely emitted in events for off-chain indexing
   */
  event AVSMetadataURIUpdated(address indexed avs, string metadataURI);

  /// @notice Emitted when an operator's registration status for an AVS is updated
  event OperatorAVSRegistrationStatusUpdated(address indexed operator, address indexed avs, OperatorAVSRegistrationStatus status);

  /**
   * @notice Called by an avs to register an operator with the avs.
   * @param operator The address of the operator to register.
   * @param operatorSignature The signature, salt, and expiry of the operator's signature.
   */
  function registerOperatorToAVS(address operator, ISignatureUtils.SignatureWithSaltAndExpiry memory operatorSignature) external;

  /**
   * @notice Called by an avs to deregister an operator with the avs.
   * @param operator The address of the operator to deregister.
   */
  function deregisterOperatorFromAVS(address operator) external;

  /**
   * @notice Called by an AVS to emit an `AVSMetadataURIUpdated` event indicating the information has updated.
   * @param metadataURI The URI for metadata associated with an AVS
   * @dev Note that the `metadataURI` is *never stored * and is only emitted in the `AVSMetadataURIUpdated` event
   */
  function updateAVSMetadataURI(string calldata metadataURI) external;

  /**
   * @notice Returns whether or not the salt has already been used by the operator.
   * @dev Salts is used in the `registerOperatorToAVS` function.
   */
  function operatorSaltIsSpent(address operator, bytes32 salt) external view returns (bool);

  /**
   * @notice Calculates the digest hash to be signed by an operator to register with an AVS
   * @param operator The account registering as an operator
   * @param avs The AVS the operator is registering to
   * @param salt A unique and single use value associated with the approver signature.
   * @param expiry Time after which the approver's signature becomes invalid
   */
  function calculateOperatorAVSRegistrationDigestHash(
    address operator,
    address avs,
    bytes32 salt,
    uint256 expiry
  ) external view returns (bytes32);

  /// @notice The EIP-712 typehash for the Registration struct used by the contract
  function OPERATOR_AVS_REGISTRATION_TYPEHASH() external view returns (bytes32);
}

contracts/interfaces/IDelegationControll.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import './IPool.sol';
import './ISignatureUtils.sol';
import './IPoolController.sol';

/**
 * @title DelegationController
 * @notice  This is the contract for delegation in BinLayer. The main functionalities of this contract are
 * - enabling anyone to register as an operator in BinLayer
 * - allowing operators to specify parameters related to stakers who delegate to them
 * - enabling any staker to delegate its stake to the operator of its choice (a given staker can only delegate to a single operator at a time)
 * - enabling a staker to undelegate its assets from the operator it is delegated to (performed as part of the withdrawal process, initiated through the PoolController.sol)
 */
interface IDelegationController is ISignatureUtils {
  // @notice Struct used for storing information about a single operator who has registered with BinLayer
  struct OperatorDetails {
    // @notice DEPRECATED -- this field is no longer used, rewards are handled in RewardsCoordinator.sol
    address __deprecated_earningsReceiver;
    /**
     * @notice Address to verify signatures when a staker wishes to delegate to the operator, as well as controlling "forced undelegations".
     * @dev Signature verification follows these rules:
     * 1) If this address is left as address(0), then any staker will be free to delegate to the operator, i.e. no signature verification will be performed.
     * 2) If this address is an EOA (i.e. it has no code), then we follow standard ECDSA signature verification for delegations to the operator.
     * 3) If this address is a contract (i.e. it has code) then we forward a call to the contract and verify that it returns the correct EIP-1271 "magic value".
     */
    address delegationApprover;
    /**
     * @notice A minimum delay -- enforced between:
     * 1) the operator signalling their intent to register for a service, via calling `Slasher.optIntoSlashing`
     * and
     * 2) the operator completing registration for the service, via the service ultimately calling `Slasher.recordFirstStakeUpdate`
     * @dev note that for a specific operator, this value *cannot decrease*, i.e. if the operator wishes to modify their OperatorDetails,
     * then they are only allowed to either increase this value or keep it the same.
     */
    uint32 stakerOptOutWindow;
  }

  /**
   * @notice Abstract struct used in calculating an EIP712 signature for a staker to approve that they (the staker themselves) delegate to a specific operator.
   * @dev Used in computing the `STAKER_DELEGATION_TYPEHASH` and as a reference in the computation of the stakerDigestHash in the `delegateToBySignature` function.
   */
  struct StakerDelegation {
    // the staker who is delegating
    address staker;
    // the operator being delegated to
    address operator;
    // the staker's nonce
    uint256 nonce;
    // the expiration timestamp (UTC) of the signature
    uint256 expiry;
  }

  /**
   * @notice Abstract struct used in calculating an EIP712 signature for an operator's delegationApprover to approve that a specific staker delegate to the operator.
   * @dev Used in computing the `DELEGATION_APPROVAL_TYPEHASH` and as a reference in the computation of the approverDigestHash in the `_delegate` function.
   */
  struct DelegationApproval {
    // the staker who is delegating
    address staker;
    // the operator being delegated to
    address operator;
    // the operator's provided salt
    bytes32 salt;
    // the expiration timestamp (UTC) of the signature
    uint256 expiry;
  }

  /**
   * Struct type used to specify an withdraw. Rather than storing the entire struct, only a hash is stored.
   * In functions that operate on existing queued withdrawals -- e.g. withdraw`, the data is resubmitted and the hash of the submitted
   * data is computed by `calculateWithdrawalRoot` and checked against the stored hash in order to confirm the integrity of the submitted data.
   */
  struct Withdrawal {
    // The address that originated the Withdrawal
    address staker;
    // The address that the staker was delegated to at the time that the Withdrawal was created
    address delegatedTo;
    // The address that can complete the Withdrawal + will receive funds when completing the withdrawal
    address withdrawer;
    // Nonce used to guarantee that otherwise identical withdrawals have unique hashes
    uint256 nonce;
    // Block timestamp when the Withdrawal was created
    uint32 startTimestamp;
    // Array of pools that the Withdrawal contains
    IPool[] pools;
    // Array containing the amount of shares in each Pool in the `pools` array
    uint256[] shares;
  }

  struct UnstakeParams {
    // Array of pools that the Unstake contains
    IPool[] pools;
    // Array containing the amount of shares in each Pool in the `pools` array
    uint256[] shares;
    // The address of the withdrawer
    address withdrawer;
  }

  // @notice Emitted when a new operator registers in BinLayer and provides their OperatorDetails.
  event OperatorRegistered(address indexed operator, OperatorDetails operatorDetails);

  /// @notice Emitted when an operator updates their OperatorDetails to @param newOperatorDetails
  event OperatorDetailsModified(address indexed operator, OperatorDetails newOperatorDetails);

  /**
   * @notice Emitted when @param operator indicates that they are updating their MetadataURI string
   * @dev Note that these strings are *never stored in storage* and are instead purely emitted in events for off-chain indexing
   */
  event OperatorMetadataURIUpdated(address indexed operator, string metadataURI);

  /// @notice Emitted whenever an operator's shares are increased for a given pool. Note that shares is the delta in the operator's shares.
  event OperatorSharesIncreased(address indexed operator, address staker, IPool pool, uint256 shares);

  /// @notice Emitted whenever an operator's shares are decreased for a given pool. Note that shares is the delta in the operator's shares.
  event OperatorSharesDecreased(address indexed operator, address staker, IPool pool, uint256 shares);

  /// @notice Emitted when @param staker delegates to @param operator.
  event StakerDelegated(address indexed staker, address indexed operator);

  /// @notice Emitted when @param staker undelegates from @param operator.
  event StakerUndelegated(address indexed staker, address indexed operator);

  /// @notice Emitted when @param staker is undelegated via a call not originating from the staker themself
  event StakerForceUndelegated(address indexed staker, address indexed operator);

  /**
   * @notice Emitted when a new withdrawal is queued.
   * @param withdrawalRoot Is the hash of the `withdrawal`.
   * @param withdrawal Is the withdrawal itself.
   */
  event WithdrawalQueued(bytes32 withdrawalRoot, Withdrawal withdrawal);

  /// @notice Emitted when a queued withdrawal is completed
  event WithdrawalCompleted(bytes32 withdrawalRoot);

  /// @notice Emitted when the `minWithdrawalDelay` variable is modified from `previousValue` to `newValue`.
  event MinWithdrawalDelaySet(uint256 previousValue, uint256 newValue);

  /// @notice Emitted when the `poolWithdrawalDelay` variable is modified from `previousValue` to `newValue`.
  event PoolWithdrawalDelaySet(IPool pool, uint256 previousValue, uint256 newValue);

  event UpdateWrappedTokenGateway(address previousGateway, address currentGateway);

  /**
   * @notice Registers the caller as an operator in BinLayer.
   * @param registeringOperatorDetails is the `OperatorDetails` for the operator.
   * @param metadataURI is a URI for the operator's metadata, i.e. a link providing more details on the operator.
   *
   * @dev Once an operator is registered, they cannot 'deregister' as an operator, and they will forever be considered "delegated to themself".
   * @dev Note that the `metadataURI` is *never stored * and is only emitted in the `OperatorMetadataURIUpdated` event
   */
  function registerAsOperator(OperatorDetails calldata registeringOperatorDetails, string calldata metadataURI) external;

  /**
   * @notice Updates an operator's stored `OperatorDetails`.
   * @param newOperatorDetails is the updated `OperatorDetails` for the operator, to replace their current OperatorDetails`.
   *
   * @dev The caller must have previously registered as an operator in BinLayer.
   */
  function modifyOperatorDetails(OperatorDetails calldata newOperatorDetails) external;

  /**
   * @notice Called by an operator to emit an `OperatorMetadataURIUpdated` event indicating the information has updated.
   * @param metadataURI The URI for metadata associated with an operator
   * @dev Note that the `metadataURI` is *never stored * and is only emitted in the `OperatorMetadataURIUpdated` event
   */
  function updateOperatorMetadataURI(string calldata metadataURI) external;

  /**
   * @notice Caller delegates their stake to an operator.
   * @param operator The account (`msg.sender`) is delegating its assets to for use in serving applications built on BinLayer.
   * @param approverSignatureAndExpiry Verifies the operator approves of this delegation
   * @param approverSalt A unique single use value tied to an individual signature.
   * @dev The approverSignatureAndExpiry is used in the event that:
   *          1) the operator's `delegationApprover` address is set to a non-zero value.
   *                  AND
   *          2) neither the operator nor their `delegationApprover` is the `msg.sender`, since in the event that the operator
   *             or their delegationApprover is the `msg.sender`, then approval is assumed.
   * @dev In the event that `approverSignatureAndExpiry` is not checked, its content is ignored entirely; it's recommended to use an empty input
   * in this case to save on complexity + gas costs
   */
  function delegateTo(address operator, SignatureWithExpiry memory approverSignatureAndExpiry, bytes32 approverSalt) external;

  /**
   * @notice Caller delegates a staker's stake to an operator with valid signatures from both parties.
   * @param staker The account delegating stake to an `operator` account
   * @param operator The account (`staker`) is delegating its assets to for use in serving applications built on BinLayer.
   * @param stakerSignatureAndExpiry Signed data from the staker authorizing delegating stake to an operator
   * @param approverSignatureAndExpiry is a parameter that will be used for verifying that the operator approves of this delegation action in the event that:
   * @param approverSalt Is a salt used to help guarantee signature uniqueness. Each salt can only be used once by a given approver.
   *
   * @dev If `staker` is an EOA, then `stakerSignature` is verified to be a valid ECDSA stakerSignature from `staker`, indicating their intention for this action.
   * @dev If `staker` is a contract, then `stakerSignature` will be checked according to EIP-1271.
   * @dev the operator's `delegationApprover` address is set to a non-zero value.
   * @dev neither the operator nor their `delegationApprover` is the `msg.sender`, since in the event that the operator or their delegationApprover
   * is the `msg.sender`, then approval is assumed.
   * @dev This function will revert if the current `block.timestamp` is equal to or exceeds the expiry
   * @dev In the case that `approverSignatureAndExpiry` is not checked, its content is ignored entirely; it's recommended to use an empty input
   * in this case to save on complexity + gas costs
   */
  function delegateToBySignature(
    address staker,
    address operator,
    SignatureWithExpiry memory stakerSignatureAndExpiry,
    SignatureWithExpiry memory approverSignatureAndExpiry,
    bytes32 approverSalt
  ) external;

  /**
   * @notice Undelegates the staker from the operator who they are delegated to.
   * @param staker The account to be undelegated.
   * @return withdrawalRoot The root of the newly queued withdrawal, if a withdrawal was queued. Otherwise just bytes32(0).
   *
   * @dev Reverts if the `staker` is also an operator, since operators are not allowed to undelegate from themselves.
   * @dev Reverts if the caller is not the staker, nor the operator who the staker is delegated to, nor the operator's specified "delegationApprover"
   * @dev Reverts if the `staker` is already undelegated.
   */
  function undelegate(address staker) external returns (bytes32[] memory withdrawalRoot);

  /**
   * Allows a staker to withdraw some shares. Withdrawn shares/pools are immediately removed
   * from the staker. If the staker is delegated, withdrawn shares/pools are also removed from
   * their operator.
   *
   * All withdrawn shares/pools are placed in a queue and can be fully withdrawn after a delay.
   */
  function unstakes(UnstakeParams[] calldata unstakeParams) external returns (bytes32[] memory);

  /**
   * @notice Used to complete the specified `withdrawal`. The caller must match `withdrawal.withdrawer`
   * @param withdrawal The Withdrawal to complete.
   * @param tokens Array in which the i-th entry specifies the `token` input to the 'withdraw' function of the i-th Pool in the `withdrawal.pools` array.
   * This input can be provided with zero length if `receiveAsTokens` is set to 'false' (since in that case, this input will be unused)
   * @param middlewareTimesIndex is the index in the operator that the staker who triggered the withdrawal was delegated to's middleware times array
   * @param receiveAsTokens If true, the shares specified in the withdrawal will be withdrawn from the specified pools themselves
   * and sent to the caller, through calls to `withdrawal.pools[i].withdraw`. If false, then the shares in the specified pools
   * will simply be transferred to the caller directly.
   * @dev middlewareTimesIndex should be calculated off chain before calling this function by finding the first index that satisfies `slasher.canWithdraw`
   * @dev beaconChainETHPool shares are non-transferrable, so if `receiveAsTokens = false` and `withdrawal.withdrawer != withdrawal.staker`, note that
   * any beaconChainETHPool shares in the `withdrawal` will be _returned to the staker_, rather than transferred to the withdrawer, unlike shares in
   * any other pools, which will be transferred to the withdrawer.
   */
  function withdraw(Withdrawal calldata withdrawal, IERC20[] calldata tokens, uint256 middlewareTimesIndex, bool receiveAsTokens) external;

  /**
   * @notice Array-ified version of `withdraw`.
   * Used to complete the specified `withdrawals`. The function caller must match `withdrawals[...].withdrawer`
   * @param withdrawals The Withdrawals to complete.
   * @param tokens Array of tokens for each Withdrawal. See `withdraw` for the usage of a single array.
   * @param middlewareTimesIndexes One index to reference per Withdrawal. See `withdraw` for the usage of a single index.
   * @param receiveAsTokens Whether or not to complete each withdrawal as tokens. See `withdraw` for the usage of a single boolean.
   * @dev See `withdraw` for relevant dev tags
   */
  function withdraws(
    Withdrawal[] calldata withdrawals,
    IERC20[][] calldata tokens,
    uint256[] calldata middlewareTimesIndexes,
    bool[] calldata receiveAsTokens
  ) external;

  /**
   * @notice Increases a staker's delegated share balance in a pool.
   * @param staker The address to increase the delegated shares for their operator.
   * @param pool The pool in which to increase the delegated shares.
   * @param shares The number of shares to increase.
   *
   * @dev *If the staker is actively delegated*, then increases the `staker`'s delegated shares in `pool` by `shares`. Otherwise does nothing.
   * @dev Callable only by the PoolController.sol.
   */
  function increaseDelegatedShares(address staker, IPool pool, uint256 shares) external;

  /**
   * @notice Decreases a staker's delegated share balance in a pool.
   * @param staker The address to increase the delegated shares for their operator.
   * @param pool The pool in which to decrease the delegated shares.
   * @param shares The number of shares to decrease.
   *
   * @dev *If the staker is actively delegated*, then decreases the `staker`'s delegated shares in `pool` by `shares`. Otherwise does nothing.
   * @dev Callable only by the PoolController.sol.
   */
  function decreaseDelegatedShares(address staker, IPool pool, uint256 shares) external;

  /**
   * @notice returns the address of the operator that `staker` is delegated to.
   * @notice Mapping: staker => operator whom the staker is currently delegated to.
   * @dev Note that returning address(0) indicates that the staker is not actively delegated to any operator.
   */
  function delegatedTo(address staker) external view returns (address);

  /**
   * @notice Returns the OperatorDetails struct associated with an `operator`.
   */
  function operatorDetails(address operator) external view returns (OperatorDetails memory);

  /**
   * @notice Returns the delegationApprover account for an operator
   */
  function delegationApprover(address operator) external view returns (address);

  /**
   * @notice Returns the stakerOptOutWindow for an operator
   */
  function stakerOptOutWindow(address operator) external view returns (uint256);

  /**
   * @notice Given array of pools, returns array of shares for the operator
   */
  function getOperatorShares(address operator, IPool[] memory pools) external view returns (uint256[] memory);

  /**
   * @notice Given a list of pools, return the minimum cooldown that must pass to withdraw
   * from all the inputted pools. Return value is >= minWithdrawalDelay as this is the global min withdrawal delay.
   * @param pools The pools to check withdrawal delays for
   */
  function getWithdrawalDelay(IPool[] calldata pools) external view returns (uint256);

  /**
   * @notice returns the total number of shares in `pool` that are delegated to `operator`.
   * @notice Mapping: operator => pool => total number of shares in the pool delegated to the operator.
   * @dev By design, the following invariant should hold for each Pool:
   * (operator's shares in delegation manager) = sum (shares above zero of all stakers delegated to operator)
   * = sum (delegateable shares of all stakers delegated to the operator)
   */
  function operatorShares(address operator, IPool pool) external view returns (uint256);

  /**
   * @notice Returns 'true' if `staker` *is* actively delegated, and 'false' otherwise.
   */
  function isDelegated(address staker) external view returns (bool);

  /**
   * @notice Returns true is an operator has previously registered for delegation.
   */
  function isOperator(address operator) external view returns (bool);

  /// @notice Mapping: staker => number of signed delegation nonces (used in `delegateToBySignature`) from the staker that the contract has already checked
  function stakerNonce(address staker) external view returns (uint256);

  /**
   * @notice Mapping: delegationApprover => 32-byte salt => whether or not the salt has already been used by the delegationApprover.
   * @dev Salts are used in the `delegateTo` and `delegateToBySignature` functions. Note that these functions only process the delegationApprover's
   * signature + the provided salt if the operator being delegated to has specified a nonzero address as their `delegationApprover`.
   */
  function delegationApproverSaltIsSpent(address _delegationApprover, bytes32 salt) external view returns (bool);

  /**
   * @notice Minimum delay enforced by this contract for completing queued withdrawals. Cooldown, and adjustable by this contract's owner,
   * up to a maximum of `MAX_WITHDRAWAL_DELAY`. Minimum value is 0 (i.e. no delay enforced).
   * Note that pools each have a separate withdrawal delay, which can be greater than this value. So the minimum cooldown that must pass
   * to withdraw a pool is MAX(minWithdrawalDelay, poolWithdrawalDelay[pool])
   */
  function minWithdrawalDelay() external view returns (uint256);

  /**
   * @notice Minimum delay enforced by this contract per Pool for completing queued withdrawals. Cooldown, and adjustable by this contract's owner,
   * up to a maximum of `MAX_WITHDRAWAL_DELAY`. Minimum value is 0 (i.e. no delay enforced).
   */
  function poolWithdrawalDelay(IPool pool) external view returns (uint256);

  /**
   * @notice Calculates the digestHash for a `staker` to sign to delegate to an `operator`
   * @param staker The signing staker
   * @param operator The operator who is being delegated to
   * @param expiry The desired expiry time of the staker's signature
   */
  function calculateCurrentStakerDelegationDigestHash(address staker, address operator, uint256 expiry) external view returns (bytes32);

  /**
   * @notice Calculates the digest hash to be signed and used in the `delegateToBySignature` function
   * @param staker The signing staker
   * @param _stakerNonce The nonce of the staker. In practice we use the staker's current nonce, stored at `stakerNonce[staker]`
   * @param operator The operator who is being delegated to
   * @param expiry The desired expiry time of the staker's signature
   */
  function calculateStakerDelegationDigestHash(
    address staker,
    uint256 _stakerNonce,
    address operator,
    uint256 expiry
  ) external view returns (bytes32);

  /**
   * @notice Calculates the digest hash to be signed by the operator's delegationApprove and used in the `delegateTo` and `delegateToBySignature` functions.
   * @param staker The account delegating their stake
   * @param operator The account receiving delegated stake
   * @param _delegationApprover the operator's `delegationApprover` who will be signing the delegationHash (in general)
   * @param approverSalt A unique and single use value associated with the approver signature.
   * @param expiry Time after which the approver's signature becomes invalid
   */
  function calculateDelegationApprovalDigestHash(
    address staker,
    address operator,
    address _delegationApprover,
    bytes32 approverSalt,
    uint256 expiry
  ) external view returns (bytes32);

  /// @notice The EIP-712 typehash for the contract's domain
  function DOMAIN_TYPEHASH() external view returns (bytes32);

  /// @notice The EIP-712 typehash for the StakerDelegation struct used by the contract
  function STAKER_DELEGATION_TYPEHASH() external view returns (bytes32);

  /// @notice The EIP-712 typehash for the DelegationApproval struct used by the contract
  function DELEGATION_APPROVAL_TYPEHASH() external view returns (bytes32);

  /**
   * @notice Getter function for the current EIP-712 domain separator for this contract.
   *
   * @dev The domain separator will change in the event of a fork that changes the ChainID.
   * @dev By introducing a domain separator the DApp developers are guaranteed that there can be no signature collision.
   * for more detailed information please read EIP-712.
   */
  function domainSeparator() external view returns (bytes32);

  /// @notice Mapping: staker => cumulative number of queued withdrawals they have ever initiated.
  /// @dev This only increments (doesn't decrement), and is used to help ensure that otherwise identical withdrawals have unique hashes.
  function cumulativeWithdrawalsQueued(address staker) external view returns (uint256);

  /// @notice Returns the keccak256 hash of `withdrawal`.
  function calculateWithdrawalRoot(Withdrawal memory withdrawal) external pure returns (bytes32);
}

