contracts/interfaces/IPausable.sol.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import '../interfaces/IPauserRegistry.sol';

/**
 * @title Adds pausability to a contract, with pausing & unpausing controlled by the `pauser` and `unpauser` of a PauserRegistry contract.
 * @notice Contracts that inherit from this contract may define their own `pause` and `unpause` (and/or related) functions.
 * These functions should be permissioned as "onlyPauser" which defers to a `PauserRegistry` for determining access control.
 * @dev Pausability is implemented using a uint256, which allows up to 256 different single bit-flags; each bit can potentially pause different functionality.
 * Inspiration for this was taken from the NearBridge design here https://etherscan.io/address/0x3FEFc5A4B1c02f21cBc8D3613643ba0635b9a873#code.
 * For the `pause` and `unpause` functions we've implemented, if you pause, you can only flip (any number of) switches to on/1 (aka "paused"), and if you unpause,
 * you can only flip (any number of) switches to off/0 (aka "paused").
 * If you want a pauseXYZ function that just flips a single bit / "pausing flag", it will:
 * 1) 'bit-wise and' (aka `&`) a flag with the current paused state (as a uint256)
 * 2) update the paused state to this new value
 * @dev We note as well that we have chosen to identify flags by their *bit index* as opposed to their numerical value, so, e.g. defining `DEPOSITS_PAUSED = 3`
 * indicates specifically that if the *third bit* of `_paused` is flipped -- i.e. it is a '1' -- then deposits should be paused
 */

interface IPausable {
  /// @notice Emitted when the `pauserRegistry` is set to `newPauserRegistry`.
  event PauserRegistrySet(IPauserRegistry pauserRegistry, IPauserRegistry newPauserRegistry);

  /// @notice Emitted when the pause is triggered by `account`, and changed to `newPausedStatus`.
  event Paused(address indexed account, uint256 newPausedStatus);

  /// @notice Emitted when the pause is lifted by `account`, and changed to `newPausedStatus`.
  event Unpaused(address indexed account, uint256 newPausedStatus);

  /// @notice Address of the `PauserRegistry` contract that this contract defers to for determining access control (for pausing).
  function pauserRegistry() external view returns (IPauserRegistry);

  /**
   * @notice This function is used to pause an BinLayer contract's functionality.
   * It is permissioned to the `pauser` address, which is expected to be a low threshold multisig.
   * @param newPausedStatus represents the new value for `_paused` to take, which means it may flip several bits at once.
   * @dev This function can only pause functionality, and thus cannot 'unflip' any bit in `_paused` from 1 to 0.
   */
  function pause(uint256 newPausedStatus) external;

  /**
   * @notice Alias for `pause(type(uint256).max)`.
   */
  function pauseAll() external;

  /**
   * @notice This function is used to unpause an BinLayer contract's functionality.
   * It is permissioned to the `unpauser` address, which is expected to be a high threshold multisig or governance contract.
   * @param newPausedStatus represents the new value for `_paused` to take, which means it may flip several bits at once.
   * @dev This function can only unpause functionality, and thus cannot 'flip' any bit in `_paused` from 0 to 1.
   */
  function unpause(uint256 newPausedStatus) external;

  /// @notice Returns the current paused status as a uint256.
  function paused() external view returns (uint256);

  /// @notice Returns 'true' if the `indexed`th bit of `_paused` is 1, and 'false' otherwise
  function paused(uint8 index) external view returns (bool);

  /// @notice Allows the unpauser to set a new pauser registry
  function setPauserRegistry(IPauserRegistry newPauserRegistry) external;
}

contracts/interfaces/IPauserRegistry.sol.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

/**
 * @title Interface for the `PauserRegistry` contract.
 */
interface IPauserRegistry {
  event PauserStatusChanged(address pauser, bool canPause);

  event UnpauserChanged(address previousUnpauser, address newUnpauser);

  /// @notice Mapping of addresses to whether they hold the pauser role.
  function isPauser(address pauser) external view returns (bool);

  /// @notice Unique address that holds the unpauser role. Capable of changing *both* the pauser and unpauser addresses.
  function unpauser() external view returns (address);
}

