// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';

/**
 * @title Minimal interface for an `Pool` contract.
 * @notice Custom `Pool` implementations may expand extensively on this interface.
 */
interface IPool {
  /**
   * @notice Used to deposit tokens into this Pool
   * @param token is the ERC20 token being deposited
   * @param amount is the amount of token being deposited
   * @dev This function is only callable by the poolController contract. It is invoked inside of the poolController's
   * `depositIntoPool` function, and individual share balances are recorded in the poolController as well.
   * @return newShares is the number of new shares issued at the current exchange ratio.
   */
  function deposit(IERC20 token, uint256 amount) external returns (uint256);

  /**
   * @notice Used to withdraw tokens from this Pool, to the `recipient`'s address
   * @param recipient is the address to receive the withdrawn funds
   * @param token is the ERC20 token being transferred out
   * @param amountShares is the amount of shares being withdrawn
   * @dev This function is only callable by the poolController contract. It is invoked inside of the poolController's
   * other functions, and individual share balances are recorded in the poolController as well.
   */
  function withdraw(address recipient, IERC20 token, uint256 amountShares) external;

  /**
   * @notice Used to convert a number of shares to the equivalent amount of underlying tokens for this pool.
   * @notice In contrast to `sharesToUnderlyingView`, this function **may** make state modifications
   * @param amountShares is the amount of shares to calculate its conversion into the underlying token
   * @return The amount of underlying tokens corresponding to the input `amountShares`
   * @dev Implementation for these functions in particular may vary significantly for different strategies
   */
  function sharesToUnderlying(uint256 amountShares) external returns (uint256);

  /**
   * @notice Used to convert an amount of underlying tokens to the equivalent amount of shares in this pool.
   * @notice In contrast to `underlyingToSharesView`, this function **may** make state modifications
   * @param amountUnderlying is the amount of `underlyingToken` to calculate its conversion into pool shares
   * @return The amount of underlying tokens corresponding to the input `amountShares`
   * @dev Implementation for these functions in particular may vary significantly for different strategies
   */
  function underlyingToShares(uint256 amountUnderlying) external returns (uint256);

  /**
   * @notice convenience function for fetching the current underlying value of all of the `user`'s shares in
   * this pool. In contrast to `userUnderlyingView`, this function **may** make state modifications
   */
  function userUnderlying(address user) external returns (uint256);

  /**
   * @notice convenience function for fetching the current total shares of `user` in this pool, by
   * querying the `poolController` contract
   */
  function shares(address user) external view returns (uint256);

  /**
   * @notice Used to convert a number of shares to the equivalent amount of underlying tokens for this pool.
   * @notice In contrast to `sharesToUnderlying`, this function guarantees no state modifications
   * @param amountShares is the amount of shares to calculate its conversion into the underlying token
   * @return The amount of shares corresponding to the input `amountUnderlying`
   * @dev Implementation for these functions in particular may vary significantly for different strategies
   */
  function sharesToUnderlyingView(uint256 amountShares) external view returns (uint256);

  /**
   * @notice Used to convert an amount of underlying tokens to the equivalent amount of shares in this pool.
   * @notice In contrast to `underlyingToShares`, this function guarantees no state modifications
   * @param amountUnderlying is the amount of `underlyingToken` to calculate its conversion into pool shares
   * @return The amount of shares corresponding to the input `amountUnderlying`
   * @dev Implementation for these functions in particular may vary significantly for different strategies
   */
  function underlyingToSharesView(uint256 amountUnderlying) external view returns (uint256);

  /**
   * @notice convenience function for fetching the current underlying value of all of the `user`'s shares in
   * this pool. In contrast to `userUnderlying`, this function guarantees no state modifications
   */
  function userUnderlyingView(address user) external view returns (uint256);

  /// @notice The underlying token for shares in this Pool
  function underlyingToken() external view returns (IERC20);

  /// @notice The total number of extant shares in this Pool
  function totalShares() external view returns (uint256);

  /// @notice Returns either a brief string explaining the pool's goal & purpose, or a link to metadata that explains in more detail.
  function explanation() external view returns (string memory);
}

contracts/interfaces/IPoolController.sol.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import './IPool.sol';
import './ISlasher.sol';
import './IDelegationController.sol';

/**
 * @title Interface for the primary entrypoint for funds into BinLayer.
 * @notice See the `PoolController` contract itself for implementation details.
 */
interface IPoolController {
  /**
   * @notice Emitted when a new deposit occurs on behalf of `staker`.
   * @param staker Is the staker who is depositing funds into BinLayer.
   * @param pool Is the pool that `staker` has deposited into.
   * @param token Is the token that `staker` deposited.
   * @param shares Is the number of new shares `staker` has been granted in `pool`.
   */
  event Deposit(address staker, IERC20 token, IPool pool, uint256 shares);

  /// @notice Emitted when `thirdPartyTransfersForbidden` is updated for a pool and value by the owner
  event UpdatedThirdPartyTransfersForbidden(IPool pool, bool value);

  /// @notice Emitted when the `poolWhitelister` is changed
  event PoolWhitelisterChanged(address previousAddress, address newAddress);

  /// @notice Emitted when a pool is added to the approved list of pools for deposit
  event PoolAddedToDepositWhitelist(IPool pool);

  /// @notice Emitted when a pool is removed from the approved list of pools for deposit
  event PoolRemovedFromDepositWhitelist(IPool pool);

  /**
   * @notice Deposits `amount` of `token` into the specified `pool`, with the resultant shares credited to `msg.sender`
   * @param pool is the specified pool where deposit is to be made,
   * @param token is the denomination in which the deposit is to be made,
   * @param amount is the amount of token to be deposited in the pool by the staker
   * @return shares The amount of new shares in the `pool` created as part of the action.
   * @dev The `msg.sender` must have previously approved this contract to transfer at least `amount` of `token` on their behalf.
   * @dev Cannot be called by an address that is 'frozen' (this function will revert if the `msg.sender` is frozen).
   *
   * WARNING: Depositing tokens that allow reentrancy (eg. ERC-777) into a pool is not recommended.  This can lead to attack vectors
   *          where the token balance and corresponding pool shares are not in sync upon reentrancy.
   */
  function depositIntoPool(IPool pool, IERC20 token, uint256 amount) external returns (uint256 shares);

  /**
   * @notice Deposits `amount` of `token` into the specified `pool`, with the resultant shares credited to `staker`
   * @param staker Staker address
   * @param pool is the specified pool where deposit is to be made,
   * @param token is the denomination in which the deposit is to be made,
   * @param amount is the amount of token to be deposited in the pool by the staker
   * @return shares The amount of new shares in the `pool` created as part of the action.
   * @dev The `msg.sender` must have previously approved this contract to transfer at least `amount` of `token` on their behalf.
   * @dev Cannot be called by an address that is 'frozen' (this function will revert if the `msg.sender` is frozen).
   *
   * WARNING: Depositing tokens that allow reentrancy (eg. ERC-777) into a pool is not recommended.  This can lead to attack vectors
   *          where the token balance and corresponding pool shares are not in sync upon reentrancy.
   */
  function depositIntoPoolWithStaker(address staker, IPool pool, IERC20 token, uint256 amount) external returns (uint256 shares);

  /**
   * @notice Used for depositing an asset into the specified pool with the resultant shares credited to `staker`,
   * who must sign off on the action.
   * Note that the assets are transferred out/from the `msg.sender`, not from the `staker`; this function is explicitly designed
   * purely to help one address deposit 'for' another.
   * @param pool is the specified pool where deposit is to be made,
   * @param token is the denomination in which the deposit is to be made,
   * @param amount is the amount of token to be deposited in the pool by the staker
   * @param staker the staker that the deposited assets will be credited to
   * @param expiry the timestamp at which the signature expires
   * @param signature is a valid signature from the `staker`. either an ECDSA signature if the `staker` is an EOA, or data to forward
   * following EIP-1271 if the `staker` is a contract
   * @return shares The amount of new shares in the `pool` created as part of the action.
   * @dev The `msg.sender` must have previously approved this contract to transfer at least `amount` of `token` on their behalf.
   * @dev A signature is required for this function to eliminate the possibility of griefing attacks, specifically those
   * targeting stakers who may be attempting to undelegate.
   * @dev Cannot be called if thirdPartyTransfersForbidden is set to true for this pool
   *
   *  WARNING: Depositing tokens that allow reentrancy (eg. ERC-777) into a pool is not recommended.  This can lead to attack vectors
   *          where the token balance and corresponding pool shares are not in sync upon reentrancy
   */
  function depositIntoPoolWithSignature(
    IPool pool,
    IERC20 token,
    uint256 amount,
    address staker,
    uint256 expiry,
    bytes memory signature
  ) external returns (uint256 shares);

  /// @notice Used by the DelegationController.sol to remove a Staker's shares from a particular pool when entering the withdrawal queue
  function removeShares(address staker, IPool pool, uint256 shares) external;

  /// @notice Used by the DelegationController.sol to award a Staker some shares that have passed through the withdrawal queue
  function addShares(address staker, IERC20 token, IPool pool, uint256 shares) external;

  /// @notice Used by the DelegationController.sol to convert withdrawn shares to tokens and send them to a recipient
  function withdrawSharesAsTokens(address recipient, IPool pool, uint256 shares, IERC20 token) external;

  /// @notice Returns the current shares of `user` in `pool`
  function stakerPoolShares(address user, IPool pool) external view returns (uint256 shares);

  /**
   * @notice Get all details on the staker's deposits and corresponding shares
   * @return (staker's pools, shares in these pools)
   */
  function getDeposits(address staker) external view returns (IPool[] memory, uint256[] memory);

  /// @notice Simple getter function that returns `stakerPoolList[staker].length`.
  function stakerPoolListLength(address staker) external view returns (uint256);

  /**
   * @notice Owner-only function that adds the provided Pools to the 'whitelist' of pools that stakers can deposit into
   * @param poolsToWhitelist Pools that will be added to the `poolIsWhitelistedForDeposit` mapping (if they aren't in it already)
   * @param thirdPartyTransfersForbiddenValues bool values to set `thirdPartyTransfersForbidden` to for each pool
   */
  function addPoolsToDepositWhitelist(IPool[] calldata poolsToWhitelist, bool[] calldata thirdPartyTransfersForbiddenValues) external;

  /**
   * @notice Owner-only function that removes the provided Pools from the 'whitelist' of pools that stakers can deposit into
   * @param poolsToRemoveFromWhitelist Pools that will be removed to the `poolIsWhitelistedForDeposit` mapping (if they are in it)
   */
  function removePoolsFromDepositWhitelist(IPool[] calldata poolsToRemoveFromWhitelist) external;

  /// @notice Returns the single, central Delegation contract of BinLayer
  function delegation() external view returns (IDelegationController);

  /// @notice Returns the single, central Slasher contract of BinLayer
  function slasher() external view returns (ISlasher);

  /// @notice Returns the address of the `poolWhitelister`
  function poolWhitelister() external view returns (address);

  /// @notice Returns bool for whether or not `pool` is whitelisted for deposit
  function poolIsWhitelistedForDeposit(IPool pool) external view returns (bool);

  /**
   * @notice Returns bool for whether or not `pool` enables credit transfers. i.e enabling
   * depositIntoPoolWithSignature calls or queueing withdrawals to a different address than the staker.
   */
  function thirdPartyTransfersForbidden(IPool pool) external view returns (bool);
}

contracts/interfaces/IRewardsCoordinator.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import './IPool.sol';
import './IPauserRegistry.sol';

/**
 * @title Interface for the `IRewardsCoordinator` contract.
 * @notice Allows AVSs to make "Rewards Submissions", which get distributed amongst the AVSs' confirmed
 * Operators and the Stakers delegated to those Operators.
 * Calculations are performed based on the completed RewardsSubmission, with the results posted in
 * a Merkle root against which Stakers & Operators can make claims.
 */
interface IRewardsCoordinator {
  /// STRUCTS ///
  /**
   * @notice A linear combination of pools and multipliers for AVSs to weigh
   * BinLayer pools.
   * @param pool The BinLayer pool to be used for the rewards submission
   * @param multiplier The weight of the pool in the rewards submission
   */
  struct PoolAndMultiplier {
    IPool pool;
    uint96 multiplier;
  }

  /**
   * Sliding Window for valid RewardsSubmission startTimestamp
   *
   * Scenario A: GENESIS_REWARDS_TIMESTAMP IS WITHIN RANGE
   *         <-----MAX_RETROACTIVE_LENGTH-----> t (block.timestamp) <---MAX_FUTURE_LENGTH--->
   *             <--------------------valid range for startTimestamp------------------------>
   *             ^
   *         GENESIS_REWARDS_TIMESTAMP
   *
   *
   * Scenario B: GENESIS_REWARDS_TIMESTAMP IS OUT OF RANGE
   *         <-----MAX_RETROACTIVE_LENGTH-----> t (block.timestamp) <---MAX_FUTURE_LENGTH--->
   *         <------------------------valid range for startTimestamp------------------------>
   *     ^
   * GENESIS_REWARDS_TIMESTAMP
   * @notice RewardsSubmission struct submitted by AVSs when making rewards for their operators and stakers
   * RewardsSubmission can be for a time range within the valid window for startTimestamp and must be within max duration.
   * See `createAVSRewardsSubmission()` for more details.
   * @param poolsAndMultipliers The pools and their relative weights
   * cannot have duplicate pools and need to be sorted in ascending address order
   * @param token The rewards token to be distributed
   * @param amount The total amount of tokens to be distributed
   * @param startTimestamp The timestamp (seconds) at which the submission range is considered for distribution
   * could start in the past or in the future but within a valid range. See the diagram above.
   * @param duration The duration of the submission range in seconds. Must be <= MAX_REWARDS_DURATION
   */
  struct RewardsSubmission {
    PoolAndMultiplier[] poolsAndMultipliers;
    IERC20 token;
    uint256 amount;
    uint32 startTimestamp;
    uint32 duration;
  }

  /**
   * @notice A distribution root is a merkle root of the distribution of earnings for a given period.
   * The RewardsCoordinator stores all historical distribution roots so that earners can claim their earnings against older roots
   * if they wish but the merkle tree contains the cumulative earnings of all earners and tokens for a given period so earners (or their claimers if set)
   * only need to claim against the latest root to claim all available earnings.
   * @param root The merkle root of the distribution
   * @param rewardsCalculationEndTimestamp The timestamp (seconds) until which rewards have been calculated
   * @param activatedAt The timestamp (seconds) at which the root can be claimed against
   */
  struct DistributionRoot {
    bytes32 root;
    uint32 rewardsCalculationEndTimestamp;
    uint32 activatedAt;
    bool disabled;
  }

  /**
   * @notice Internal leaf in the merkle tree for the earner's account leaf
   * @param earner The address of the earner
   * @param earnerTokenRoot The merkle root of the earner's token subtree
   * Each leaf in the earner's token subtree is a TokenTreeMerkleLeaf
   */
  struct EarnerTreeMerkleLeaf {
    address earner;
    bytes32 earnerTokenRoot;
  }

  /**
   * @notice The actual leaves in the distribution merkle tree specifying the token earnings
   * for the respective earner's subtree. Each leaf is a claimable amount of a token for an earner.
   * @param token The token for which the earnings are being claimed
   * @param cumulativeEarnings The cumulative earnings of the earner for the token
   */
  struct TokenTreeMerkleLeaf {
    IERC20 token;
    uint256 cumulativeEarnings;
  }

  /**
   * @notice A claim against a distribution root called by an
   * earners claimer (could be the earner themselves). Each token claim will claim the difference
   * between the cumulativeEarnings of the earner and the cumulativeClaimed of the claimer.
   * Each claim can specify which of the earner's earned tokens they want to claim.
   * See `processClaim()` for more details.
   * @param rootIndex The index of the root in the list of DistributionRoots
   * @param earnerIndex The index of the earner's account root in the merkle tree
   * @param earnerTreeProof The proof of the earner's EarnerTreeMerkleLeaf against the merkle root
   * @param earnerLeaf The earner's EarnerTreeMerkleLeaf struct, providing the earner address and earnerTokenRoot
   * @param tokenIndices The indices of the token leaves in the earner's subtree
   * @param tokenTreeProofs The proofs of the token leaves against the earner's earnerTokenRoot
   * @param tokenLeaves The token leaves to be claimed
   * @dev The merkle tree is structured with the merkle root at the top and EarnerTreeMerkleLeaf as internal leaves
   * in the tree. Each earner leaf has its own subtree with TokenTreeMerkleLeaf as leaves in the subtree.
   * To prove a claim against a specified rootIndex(which specifies the distributionRoot being used),
   * the claim will first verify inclusion of the earner leaf in the tree against _distributionRoots[rootIndex].root.
   * Then for each token, it will verify inclusion of the token leaf in the earner's subtree against the earner's earnerTokenRoot.
   */
  struct RewardsMerkleClaim {
    uint32 rootIndex;
    uint32 earnerIndex;
    bytes earnerTreeProof;
    EarnerTreeMerkleLeaf earnerLeaf;
    uint32[] tokenIndices;
    bytes[] tokenTreeProofs;
    TokenTreeMerkleLeaf[] tokenLeaves;
  }

  /// EVENTS ///

  /// @notice emitted when an AVS creates a valid RewardsSubmission
  event AVSRewardsSubmissionCreated(
    address indexed avs,
    uint256 indexed submissionNonce,
    bytes32 indexed rewardsSubmissionHash,
    RewardsSubmission rewardsSubmission
  );
  /// @notice emitted when a valid RewardsSubmission is created for all stakers by a valid submitter
  event RewardsSubmissionForAllCreated(
    address indexed submitter,
    uint256 indexed submissionNonce,
    bytes32 indexed rewardsSubmissionHash,
    RewardsSubmission rewardsSubmission
  );
  /// @notice rewardsUpdater is responsible for submiting DistributionRoots, only owner can set rewardsUpdater
  event RewardsUpdaterSet(address indexed oldRewardsUpdater, address indexed newRewardsUpdater);
  event RewardsForAllSubmitterSet(address indexed rewardsForAllSubmitter, bool indexed oldValue, bool indexed newValue);
  event ActivationDelaySet(uint32 oldActivationDelay, uint32 newActivationDelay);
  event GlobalCommissionBipsSet(uint16 oldGlobalCommissionBips, uint16 newGlobalCommissionBips);
  event ClaimerForSet(address indexed earner, address indexed oldClaimer, address indexed claimer);
  /// @notice rootIndex is the specific array index of the newly created root in the storage array
  event DistributionRootSubmitted(
    uint32 indexed rootIndex,
    bytes32 indexed root,
    uint32 indexed rewardsCalculationEndTimestamp,
    uint32 activatedAt
  );
  event DistributionRootDisabled(uint32 indexed rootIndex);
  /// @notice root is one of the submitted distribution roots that was claimed against
  event RewardsClaimed(
    bytes32 root,
    address indexed earner,
    address indexed claimer,
    address indexed recipient,
    IERC20 token,
    uint256 claimedAmount
  );

  /**
   *
   *                         VIEW FUNCTIONS
   *
   */

  /// @notice The address of the entity that can update the contract with new merkle roots
  function rewardsUpdater() external view returns (address);

  /**
   * @notice The interval in seconds at which the calculation for a RewardsSubmission distribution is done.
   * @dev Rewards Submission durations must be multiples of this interval.
   */
  function CALCULATION_INTERVAL_SECONDS() external view returns (uint32);

  /// @notice The maximum amount of time (seconds) that a RewardsSubmission can span over
  function MAX_REWARDS_DURATION() external view returns (uint32);

  /// @notice max amount of time (seconds) that a submission can start in the past
  function MAX_RETROACTIVE_LENGTH() external view returns (uint32);

  /// @notice max amount of time (seconds) that a submission can start in the future
  function MAX_FUTURE_LENGTH() external view returns (uint32);

  /// @notice absolute min timestamp (seconds) that a submission can start at
  function GENESIS_REWARDS_TIMESTAMP() external view returns (uint32);

  /// @notice Delay in timestamp (seconds) before a posted root can be claimed against
  function activationDelay() external view returns (uint32);

  /// @notice Mapping: earner => the address of the entity who can call `processClaim` on behalf of the earner
  function claimerFor(address earner) external view returns (address);

  /// @notice Mapping: claimer => token => total amount claimed
  function cumulativeClaimed(address claimer, IERC20 token) external view returns (uint256);

  /// @notice the commission for all operators across all avss
  function globalOperatorCommissionBips() external view returns (uint16);

  /// @notice the commission for a specific operator for a specific avs
  /// NOTE: Currently unused and simply returns the globalOperatorCommissionBips value but will be used in future release
  function operatorCommissionBips(address operator, address avs) external view returns (uint16);

  /// @notice return the hash of the earner's leaf
  function calculateEarnerLeafHash(EarnerTreeMerkleLeaf calldata leaf) external pure returns (bytes32);

  /// @notice returns the hash of the earner's token leaf
  function calculateTokenLeafHash(TokenTreeMerkleLeaf calldata leaf) external pure returns (bytes32);

  /// @notice returns 'true' if the claim would currently pass the check in `processClaims`
  /// but will revert if not valid
  function checkClaim(RewardsMerkleClaim calldata claim) external view returns (bool);

  /// @notice The timestamp until which RewardsSubmissions have been calculated
  function currRewardsCalculationEndTimestamp() external view returns (uint32);

  /// @notice loop through distribution roots from reverse and return hash
  function getRootIndexFromHash(bytes32 rootHash) external view returns (uint32);

  /// @notice returns the number of distribution roots posted
  function getDistributionRootsLength() external view returns (uint256);

  /// @notice returns the distributionRoot at the specified index
  function getDistributionRootAtIndex(uint256 index) external view returns (DistributionRoot memory);

  /// @notice returns the current distributionRoot
  function getCurrentDistributionRoot() external view returns (DistributionRoot memory);

  /**
   *
   *                         EXTERNAL FUNCTIONS
   *
   */

  /**
   * @notice Creates a new rewards submission on behalf of an AVS, to be split amongst the
   * set of stakers delegated to operators who are registered to the `avs`
   * @param rewardsSubmissions The rewards submissions being created
   * @dev Expected to be called by the ServiceManager of the AVS on behalf of which the submission is being made
   * @dev The duration of the `rewardsSubmission` cannot exceed `MAX_REWARDS_DURATION`
   * @dev The tokens are sent to the `RewardsCoordinator` contract
   * @dev pools must be in ascending order of addresses to check for duplicates
   * @dev This function will revert if the `rewardsSubmission` is malformed,
   * e.g. if the `pools` and `weights` arrays are of non-equal lengths
   */
  function createAVSRewardsSubmission(RewardsSubmission[] calldata rewardsSubmissions) external;

  /**
   * @notice similar to `createAVSRewardsSubmission` except the rewards are split amongst *all* stakers
   * rather than just those delegated to operators who are registered to a single avs and is
   * a permissioned call based on isRewardsForAllSubmitter mapping.
   */
  function createRewardsForAllSubmission(RewardsSubmission[] calldata rewardsSubmission) external;

  /**
   * @notice Claim rewards against a given root (read from _distributionRoots[claim.rootIndex]).
   * Earnings are cumulative so earners don't have to claim against all distribution roots they have earnings for,
   * they can simply claim against the latest root and the contract will calculate the difference between
   * their cumulativeEarnings and cumulativeClaimed. This difference is then transferred to recipient address.
   * @param claim The RewardsMerkleClaim to be processed.
   * Contains the root index, earner, token leaves, and required proofs
   * @param recipient The address recipient that receives the ERC20 rewards
   * @dev only callable by the valid claimer, that is
   * if claimerFor[claim.earner] is address(0) then only the earner can claim, otherwise only
   * claimerFor[claim.earner] can claim the rewards.
   */
  function processClaim(RewardsMerkleClaim calldata claim, address recipient) external;

  /**
   * @notice Creates a new distribution root. activatedAt is set to block.timestamp + activationDelay
   * @param root The merkle root of the distribution
   * @param rewardsCalculationEndTimestamp The timestamp (seconds) until which rewards have been calculated
   * @dev Only callable by the rewardsUpdater
   */
  function submitRoot(bytes32 root, uint32 rewardsCalculationEndTimestamp) external;

  /**
   * @notice allow the rewardsUpdater to disable/cancel a pending root submission in case of an error
   * @param rootIndex The index of the root to be disabled
   */
  function disableRoot(uint32 rootIndex) external;

  /**
   * @notice Sets the address of the entity that can call `processClaim` on behalf of the earner (msg.sender)
   * @param claimer The address of the entity that can claim rewards on behalf of the earner
   * @dev Only callable by the `earner`
   */
  function setClaimerFor(address claimer) external;

  /**
   * @notice Sets the delay in timestamp before a posted root can be claimed against
   * @param _activationDelay Delay in timestamp (seconds) before a posted root can be claimed against
   * @dev Only callable by the contract owner
   */
  function setActivationDelay(uint32 _activationDelay) external;

  /**
   * @notice Sets the global commission for all operators across all avss
   * @param _globalCommissionBips The commission for all operators across all avss
   * @dev Only callable by the contract owner
   */
  function setGlobalOperatorCommission(uint16 _globalCommissionBips) external;

  /**
   * @notice Sets the permissioned `rewardsUpdater` address which can post new roots
   * @dev Only callable by the contract owner
   */
  function setRewardsUpdater(address _rewardsUpdater) external;

  /**
   * @notice Sets the permissioned `rewardsForAllSubmitter` address which can submit createRewardsForAllSubmission
   * @dev Only callable by the contract owner
   * @param _submitter The address of the rewardsForAllSubmitter
   * @param _newValue The new value for isRewardsForAllSubmitter
   */
  function setRewardsForAllSubmitter(address _submitter, bool _newValue) external;
}

contracts/interfaces/ISignatureUtils.sol.content:
// SPDX-License-Identifier: LGPL-3.0
pragma solidity 0.8.20;

/**
 * @title The interface for common signature utilities.
 */
interface ISignatureUtils {
  // @notice Struct that bundles together a signature and an expiration time for the signature. Used primarily for stack management.
  struct SignatureWithExpiry {
    // the signature itself, formatted as a single bytes object
    bytes signature;
    // the expiration timestamp (UTC) of the signature
    uint256 expiry;
  }

  // @notice Struct that bundles together a signature, a salt for uniqueness, and an expiration time for the signature. Used primarily for stack management.
  struct SignatureWithSaltAndExpiry {
    // the signature itself, formatted as a single bytes object
    bytes signature;
    // the salt used to generate the signature
    bytes32 salt;
    // the expiration timestamp (UTC) of the signature
    uint256 expiry;
  }
}

